    color([0.35, 0.15, 0.05])
    union() {
        // Upper leg
        translate([side * body_width/3, front * body_depth/3, leg_height/2])
        rotate([0, side * 10, front * 10])
        cylinder(h=leg_height, d1=10, d2=8);

        // Foot
        translate([side * body_width/3, front * body_depth/3, 2])
        scale([1.5, 2, 0.5])
        sphere(d=10);

        // Toes
