// Phione Pokemon in realistic style
// Size: ~80mm height

module phione() {
    // Main body - translucent blue sphere
    color([0.3, 0.6, 0.9, 0.8])
    sphere(30);

    // Head details
    translate([0, 0, 25]) {
        // Face mask - lighter blue
        color([0.5, 0.7, 1])
        difference() {
            sphere(28);
            translate([0, 0, -5])
            sphere(25);
        }

        // Eyes
        for(x = [-12, 12]) {
            translate([x, 8, 8]) {
                // Eye whites
                color([1, 1, 1])
                sphere(4);
                // Pupils
                translate([0, 0, 3])
                color([0.2, 0.2, 0.3])
                sphere(2);
            }
        }

        // Mouth
        translate([0, -8, 0])
        rotate([90, 0, 0])
        color([0.8, 0.5, 0.5])
        cylinder(h=1, r1=4, r2=1);
    }

    // Floating antenna/crest
    translate([0, 0, 45])
    color([0.4, 0.7, 0.9])
    for(i = [0:4]) {
        rotate([0, 0, i*72])
        translate([0, 8, 0])
        rotate([0, 0, 45])
        scale([1, 0.3, 0.1])
        sphere(10);
    }

    // Arms - simple fins
    module fin() {
        translate([0, 0, -2])
        color([0.4, 0.7, 0.9])
        difference() {
            scale([1, 2, 0.3])
            sphere(6);
            translate([0, 4, 0])
            cube([12, 8, 2], center=true);
        }
    }

    // Left fin
    translate([-25, 0, 10])
    rotate([0, 0, -30])
    fin();

    // Right fin
    translate([25, 0, 10])
    mirror([1, 0, 0])
    rotate([0, 0, -30])
    fin();

    // Lower body tentacles
    module tentacle() {
        color([0.3, 0.6, 0.9, 0.7])
        rotate([90, 0, 0])
        cylinder(h=25, r1=4, r2=1);
    }

    // Create 5 tentacles
    for(i = [0:4]) {
        rotate([0, 0, i*72])
        translate([0, 20, -30])
        tentacle();
    }

    // Back fin
    translate([0, -25, 0])
    rotate([0, 0, 180])
    color([0.3, 0.5, 0.8])
    difference() {
        scale([2, 1.5, 0.2])
        sphere(8);
        translate([0, 0, -1])
        cube([16, 12, 2], center=true);
    }

    // Markings on body
    color([1, 1, 1, 0.3])
    for(i = [0:3]) {
        rotate([0, 0, i*90])
        translate([0, 15, 0])
        scale([1, 0.3, 0.1])
        sphere(8);
    }
}

// Scale to 80mm height
scale(0.8)
phione();

// Manaphy Pokemon - Realistic Style
// Medium complexity OpenSCAD model for 3D printing

// Colors
color_body = "#87CEEB";
color_yellow = "#FFD700";
color_red = "#DC143C";
color_blue = "#1E90FF";

// Main parameters
scale_factor = 0.6;
body_height = 80 * scale_factor;
head_radius = 20 * scale_factor;
body_radius = 25 * scale_factor;

// Head module
module head() {
    color(color_body) {
        // Main head sphere
        translate([0, 0, body_height * 0.8])
            sphere(r = head_radius, $fn = 32);

        // Forehead bump
        translate([0, 0, body_height * 0.8 + head_radius * 0.8])
            sphere(r = head_radius * 0.6, $fn = 32);
    }
}

// Eyes module
module eyes() {
    // Left eye
    translate([-head_radius * 0.4, head_radius * 0.7, body_height * 0.8])
        color(color_blue)
            sphere(r = head_radius * 0.15, $fn = 16);

    // Right eye
    translate([head_radius * 0.4, head_radius * 0.7, body_height * 0.8])
        color(color_blue)
            sphere(r = head_radius * 0.15, $fn = 16);

    // Eye highlights
    translate([-head_radius * 0.35, head_radius * 0.75, body_height * 0.8 + 1])
        color("white")
            sphere(r = head_radius * 0.05, $fn = 8);

    translate([head_radius * 0.45, head_radius * 0.75, body_height * 0.8 + 1])
        color("white")
            sphere(r = head_radius * 0.05, $fn = 8);
}

// Antenna module
module antenna() {
    color(color_yellow) {
        // Left antenna
        translate([-head_radius * 0.6, 0, body_height * 0.8 + head_radius * 0.8])
            rotate([20, 0, -30])
                cylinder(h = head_radius * 1.5, r = head_radius * 0.1, $fn = 8);

        // Right antenna
        translate([head_radius * 0.6, 0, body_height * 0.8 + head_radius * 0.8])
            rotate([20, 0, 30])
                cylinder(h = head_radius * 1.5, r = head_radius * 0.1, $fn = 8);
    }
}

// Body module
module body() {
    color(color_body) {
        // Main body
        translate([0, 0, body_height * 0.3])
            sphere(r = body_radius, $fn = 32);

        // Lower body extension
        translate([0, 0, body_height * 0.15])
            cylinder(h = body_height * 0.3, r = body_radius * 0.8, $fn = 32);
    }
}

// Arms module
module arms() {
    color(color_body) {
        // Left arm
        translate([-body_radius * 0.9, 0, body_height * 0.5])
            rotate([0, -45, -30])
                cylinder(h = body_radius * 0.8, r = body_radius * 0.25, $fn = 8);

        // Right arm
        translate([body_radius * 0.9, 0, body_height * 0.5])
            rotate([0, -45, 30])
                cylinder(h = body_radius * 0.8, r = body_radius * 0.25, $fn = 8);
    }
}

// Tail module
module tail() {
    color(color_body) {
        // Main tail
        translate([0, -body_radius * 1.2, body_height * 0.2])
            rotate([90, 0, 0])
                cylinder(h = body_radius * 1.5, r1 = body_radius * 0.8, r2 = body_radius * 0.3, $fn = 16);

        // Tail flukes
        for(i = [0:1]) {
            mirror([i * 2 - 1, 0, 0])
                translate([0, -body_radius * 2.2, body_height * 0.2])
                    rotate([90, 0, 45])
                        cube([body_radius * 0.8, body_radius * 0.4, body_radius * 0.1], center = true);
        }
    }
}

// Chest gem
module chest_gem() {
    color(color_red) {
        translate([0, body_radius * 0.5, body_height * 0.5])
            sphere(r = body_radius * 0.2, $fn = 16);
    }
}

// Feet module
module feet() {
    color(color_body) {
        // Left foot
        translate([-body_radius * 0.5, 0, 0])
            cube([body_radius * 0.4, body_radius * 0.6, body_radius * 0.2], center = true);

        // Right foot
        translate([body_radius * 0.5, 0, 0])
            cube([body_radius * 0.4, body_radius * 0.6, body_radius * 0.2], center = true);
    }
}

// Assembly
union() {
    body();
    head();
    eyes();
    antenna();
    arms();
    tail();
    chest_gem();
    feet();
}

// Darkrai - Realistic Style, Medium Complexity
// 3D printable model, ~80mm tall

// Main color scheme
$darkrai_body = [0.1, 0.1, 0.1];      // Dark grey/black
$darkrai_head = [0.05, 0.05, 0.05];   // Slightly darker for head
$darkrai_eyes = [1, 0, 0];            // Red eyes
$darkrai_mouth = [0, 0, 0];           // Pure black mouth
$darkrai_collar = [0.15, 0.15, 0.15]; // Lighter grey collar

// Helper module for rounded box
module rounded_box(size, radius, center=false) {
    translate([0, 0, center ? size[2]/2 : 0])
    hull() {
        for(x = [0, size[0]-2*radius])
            for(y = [0, size[1]-2*radius])
                translate([x+radius, y+radius, radius])
                sphere(r=radius);
        for(x = [0, size[0]-2*radius])
            for(z = [0, size[2]-2*radius])
                translate([x+radius, radius, z+radius])
                sphere(r=radius);
        for(y = [0, size[1]-2*radius])
            for(z = [0, size[2]-2*radius])
                translate([radius, y+radius, z+radius])
                sphere(r=radius);
    }
}

// Head module
module darkrai_head() {
    color($darkrai_head) {
        // Main head shape
        translate([0, 0, 15])
        scale([1.2, 1, 1.5])
        sphere(d=28);

        // Pointed top
        translate([0, 0, 32])
        scale([0.8, 0.8, 1.5])
        sphere(d=12);

        // Side horns
        for(side = [-1, 1]) {
            translate([side*12, 0, 25])
            rotate([0, side*20, 0])
            scale([0.8, 0.8, 2])
            cylinder(h=18, d1=6, d2=3);
        }
    }

    // Eyes
    color($darkrai_eyes) {
        for(side = [-1, 1]) {
            translate([side*8, 10, 22])
            scale([1, 0.3, 1])
            sphere(d=6);
        }
    }

    // Mouth
    color($darkrai_mouth) {
        translate([0, 11, 16])
        rotate([90, 0, 0])
        cylinder(h=0.5, d=12);
    }
}

// Collar module
module darkrai_collar() {
    color($darkrai_collar) {
        // Main collar
        translate([0, 0, 10])
        difference() {
            cylinder(h=8, d1=30, d2=35);
            cylinder(h=9, d=20);
        }

        // Collar spikes
        for(i = [0:11]) {
            rotate([0, 0, i*30])
            translate([15, 0, 14])
            rotate([0, 90, 0])
            cylinder(h=8, d1=3, d2=1);
        }
    }
}

// Body module
module darkrai_body() {
    color($darkrai_body) {
        // Torso
        translate([0, 0, 0])
        scale([1.2, 0.8, 1.5])
        rounded_box([25, 20, 35], 5, true);

        // Arms
        for(side = [-1, 1]) {
            translate([side*10, 8, 5])
            rotate([0, side*15, 0])
            scale([0.8, 0.8, 1.2])
            cylinder(h=20, d1=8, d2=6);
        }
    }
}

// Legs module
module darkrai_legs() {
    color($darkrai_body) {
        for(side = [-1, 1]) {
            translate([side*5, -5, -15])
            scale([1, 1, 1.5])
            cylinder(h=20, d=8);
        }
    }
}

// Shadow wings module
module darkrai_wings() {
    color($darkrai_body) {
        for(side = [-1, 1]) {
            // Main wing
            translate([side*15, -5, 5])
            rotate([0, side*20, 0])
            scale([1, 0.2, 2])
            rounded_box([25, 3, 30], 3);

            // Wing tips
            translate([side*25, -5, 15])
            rotate([0, side*30, side*10])
            scale([0.6, 0.2, 1.5])
            rounded_box([15, 2, 20], 2);
        }
    }
}

// Assembly
union() {
    darkrai_head();
    darkrai_collar();
    darkrai_body();
    darkrai_legs();
    darkrai_wings();
}

// Realistic Shaymin OpenSCAD Model (Medium Complexity)
// Features: head, ears, eyes, nose, mouth, neck, body, legs, tail, flowers, grass back

$fn = 32; // Smooth curves for 3D printing

// Overall scale for 3D printing (70mm height)
scale_factor = 0.7;

// Main colors
color_body = [0.82, 0.71, 0.55];      // Light tan/fur
color_green = [0.39, 0.78, 0.48];     // Grass green
color_pink = [0.96, 0.76, 0.82];      // Flower pink
color_red = [0.78, 0.22, 0.22];       // Eye red
color_black = [0.1, 0.1, 0.1];        // Black details

module shaymin() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Head
            translate([0, 0, 50]) {
                color(color_body) {
                    sphere(12);

                    // Snout
                    translate([0, 12, -2]) {
                        scale([1, 1.5, 0.8]) sphere(6);
                    }

                    // Ears (2)
                    for(side = [-1, 1]) {
                        translate([side * 10, 0, 8]) {
                            rotate([0, side * 20, 0]) {
                                scale([1, 0.5, 2]) sphere(6);
                            }
                        }
                    }

                    // Eyes
                    for(side = [-1, 1]) {
                        translate([side * 6, 10, 2]) {
                            color(color_red) sphere(2);
                            translate([0, 1, 0.5]) color(color_black) sphere(1);
                        }
                    }

                    // Nose
                    translate([0, 16, 0]) {
                        color(color_black) sphere(1.5);
                    }

                    // Mouth
                    translate([0, 15, -4]) {
                        color(color_black) {
                            rotate([90, 0, 0]) 
                            cylinder(h=0.5, r1=2, r2=0);
                        }
                    }
                }
            }

            // Neck
            translate([0, 0, 35]) {
                color(color_body) {
                    cylinder(h=10, r1=8, r2=6);
                }
            }

            // Body
            translate([0, -5, 20]) {
                color(color_body) {
                    scale([1.2, 1.5, 1]) sphere(15);
                }
            }

            // Legs (4)
            for(side = [-1, 1]) {
                for(fb = [-1, 1]) {
                    translate([side * 12, fb * 8, 10]) {
                        color(color_body) {
                            rotate([90, 0, 0]) {
                                cylinder(h=18, r=4);
                            }
                        }
                    }
                }
            }

            // Tail
            translate([0, -20, 18]) {
                color(color_body) {
                    rotate([45, 0, 0]) {
                        scale([0.8, 0.8, 1.5]) sphere(10);
                    }
                }
            }

            // Grass back
            translate([0, -5, 30]) {
                color(color_green) {
                    // Main grass mound
                    difference() {
                        scale([1.5, 1.8, 0.8]) sphere(15);
                        translate([0, 0, 10]) cube([40, 40, 20], center=true);
                    }

                    // Grass spikes
                    for(i = [0:35]) {
                        rotate([0, 0, i * 10.3]) {
                            translate([12 + sin(i) * 3, 0, 30]) {
                                rotate([90, 0, 0]) {
                                    scale([0.3, 0.3, 1 + sin(i*3) * 0.5]) {
                                        cylinder(h=8, r1=1, r2=0);
                                    }
                                }
                            }
                        }
                    }
                }
            }

            // Flowers on grass (2 pink flowers)
            for(angle = [0, 120, 240]) {
                rotate([0, 0, angle]) {
                    translate([15, 0, 35]) {
                        color(color_pink) {
                            // Flower petals
                            for(p = [0:45:315]) {
                                rotate([0, 0, p]) {
                                    translate([3, 0, 0]) {
                                        scale([0.3, 1.2, 0.1]) sphere(2);
                                    }
                                }
                            }
                            // Flower center
                            translate([0, 0, 1]) {
                                color([0.98, 0.92, 0.36]) sphere(1.5);
                            }
                        }
                    }
                }
            }
        }
    }
}

// Render the model
shaymin();

// Victini in realistic style - medium complexity
// 3D printable model with proper wall thickness

$fn = 32;
