color_yellow = [1, 0.9, 0.2];
color_blue = [0.2, 0.5, 0.9];
color_black = [0.1, 0.1, 0.1];
color_white = [0.95, 0.95, 0.95];

module victini() {
    scale([scale_factor, scale_factor, scale_factor])
    union() {
        // Main body
        color(color_orange)
        difference() {
            sphere(d = 20);
            translate([0, 0, -10]) cube([30, 30, 10], center = true);
        }

        // Head
        color(color_orange)
        translate([0, 0, 12])
        sphere(d = 18);

        // Ears
        module ear(x_pos) {
            color(color_orange)
            translate([x_pos * 7, 0, 20])
            rotate([x_pos * -15, 0, 0])
            scale([0.8, 0.3, 2])
            sphere(d = 8);

            color(color_yellow)
            translate([x_pos * 7.5, 0, 23])
            rotate([x_pos * -15, 0, 0])
            scale([0.5, 0.2, 1.5])
            sphere(d = 6);
        }

        ear(1);
        ear(-1);

        // Eyes
        module eye(x_pos) {
            color(color_blue)
            translate([x_pos * 5, 4, 15])
            sphere(d = 4.5);

            color(color_white)
            translate([x_pos * 5.5, 4.5, 16])
            sphere(d = 2);

            color(color_black)
            translate([x_pos * 5.7, 4.7, 17])
            sphere(d = 1);
        }

        eye(1);
        eye(-1);

        // Mouth
        color(color_black)
        translate([0, 6, 12])
        scale([3, 0.5, 2])
        sphere(d = 2);

        // Arms
        module arm(x_pos) {
            color(color_orange)
            translate([x_pos * 8, 7, 0])
            rotate([0, 0, x_pos * 30])
            scale([0.7, 0.7, 1.2])
            sphere(d = 8);
        }

        arm(1);
        arm(-1);

        // Feet
        module foot(x_pos) {
            color(color_orange)
            translate([x_pos * 5, 0, -7])
            scale([1.2, 2, 0.8])
            sphere(d = 8);
        }

        foot(1);
        foot(-1);

        // Wings
        module wing(x_pos) {
            color(color_yellow)
            translate([x_pos * 10, -5, 2])
            rotate([0, 0, x_pos * -20])
            scale([0.3, 2, 1.5])
            sphere(d = 12);
        }

        wing(1);
        wing(-1);

        // Tail (V-shaped)
        module tail_section(angle) {
            color(color_orange)
            rotate([0, 0, angle])
            translate([0, -12, -5])
            scale([1, 0.8, 2.5])
            sphere(d = 8);

            color(color_yellow)
            rotate([0, 0, angle])
            translate([0, -14, -3])
            scale([0.6, 0.5, 2])
            sphere(d = 6);
        }

        tail_section(20);
        tail_section(-20);
        tail_section(0);

        // Chest pattern
        color(color_yellow)
        translate([0, 4, 8])
        scale([2.5, 3, 0.5])
        sphere(d = 8);

        // Back pattern
        color(color_yellow)
        translate([0, -6, 8])
        scale([4, 2, 0.5])
        sphere(d = 8);

        // Fingers
        module fingers(x_pos) {
            for(i = [-1:1]) {
                color(color_orange)
                translate([x_pos * 9, 9 + i * 1.5, -2])
                scale([0.3, 0.3, 1])
                sphere(d = 3);
            }
        }

        fingers(1);
        fingers(-1);
    }
}

// Generate the model
victini();

