scale_factor = 2;
body_color = [0, 0.5, 0.2];
chest_color = [0.9, 0.9, 0.9];
eye_color = [1, 1, 1];
pupil_color = [0, 0, 0];
leaf_color = [0, 0.7, 0.2];
yellow_color = [1, 0.8, 0];

// Main module
module servine() {
    scale([scale_factor, scale_factor, scale_factor]) {
        // Body
        union() {
            // Torso
            color(body_color) {
                translate([0, 0, 10])
                    scale([1.2, 0.8, 1.5])
                    sphere(r=10);

                // Lower body
                translate([0, 0, 0])
                    cylinder(h=10, r1=10, r2=8);
            }

            // Chest
            color(chest_color) {
                translate([0, -5, 10])
                    scale([1, 0.5, 1.2])
                    sphere(r=8);
            }

            // Head
            color(body_color) {
                translate([0, 15, 25])
                    scale([0.9, 1.2, 1])
                    sphere(r=10);
            }

            // Snout
            color(body_color) {
                translate([0, 23, 22])
                    rotate([30, 0, 0])
                    scale([0.8, 1.5, 0.8])
                    cylinder(h=8, r1=4, r2=3);
            }

            // Ears (leaf shaped)
            color(leaf_color) {
                // Left ear
                translate([-8, 12, 30])
                    rotate([0, 0, -30])
                    scale([0.3, 1, 0.1])
                    cylinder(h=12, r=3);

                // Right ear
                translate([8, 12, 30])
                    rotate([0, 0, 30])
                    scale([0.3, 1, 0.1])
                    cylinder(h=12, r=3);
            }

            // Eyes
            color(eye_color) {
                translate([-4, 18, 26])
                    sphere(r=2);
                translate([4, 18, 26])
                    sphere(r=2);
            }

            // Pupils
            color(pupil_color) {
                translate([-4, 19.5, 26.5])
                    sphere(r=1);
                translate([4, 19.5, 26.5])
                    sphere(r=1);
            }

            // Arms
            color(body_color) {
                // Left arm
                translate([-12, 5, 15])
                    rotate([0, 0, 45])
                    cylinder(h=12, r=3);

                // Right arm
                translate([12, 5, 15])
                    rotate([0, 0, -45])
                    cylinder(h=12, r=3);

                // Hands
                translate([-18, 0, 15])
                    sphere(r=3);
                translate([18, 0, 15])
                    sphere(r=3);
            }

            // Legs
            color(body_color) {
                // Left leg
                translate([-6, -5, 0])
                    cylinder(h=8, r=3);

                // Right leg
                translate([6, -5, 0])
                    cylinder(h=8, r=3);

                // Feet
                translate([-6, -8, 0])
                    scale([1.5, 2, 0.5])
                    sphere(r=3);
                translate([6, -8, 0])
                    scale([1.5, 2, 0.5])
                    sphere(r=3);
            }

            // Tail (vine)
            color(leaf_color) {
                translate([0, -12, 5])
                    rotate([0, 0, 0])
                    cylinder(h=20, r1=4, r2=2);

                // Tail curve
                translate([0, -25, 8])
                    rotate([30, 0, 0])
                    cylinder(h=15, r1=2, r2=1);
            }

            // Leaf collar
            color(leaf_color) {
                for(i = [0:45:315]) {
                    rotate([0, 0, i])
                        translate([0, 8, 18])
                        rotate([0, 30, 0])
                        scale([0.2, 1, 0.5])
                        cylinder(h=8, r=2);
                }
            }

            // Yellow markings
            color(yellow_color) {
                // Eye markings
                translate([-7, 15, 25])
                    scale([0.5, 2, 0.2])
                    cylinder(h=6, r=1);
                translate([7, 15, 25])
                    scale([0.5, 2, 0.2])
                    cylinder(h=6, r=1);

                // Tail tip
                translate([0, -35, 7])
                    sphere(r=2);
            }
        }
    }
}

// Generate the model
servine();

// Serperior - Realistic Style Medium Complexity
// 3D printable model with proper wall thickness and no overhangs

$fn = 32;
wall_thickness = 2;

module head() {
    color([0.2, 0.6, 0.3]) {
        // Main head shape
        translate([0, 0, 15]) {
            rotate([0, 0, 0]) {
                scale([1, 1.2, 1.3]) sphere(r=8);
            }
        }

        // Snout extension
        translate([0, 10, 14]) {
            scale([0.8, 1.5, 0.7]) sphere(r=6);
        }

        // Lower jaw
        translate([0, 8, 10]) {
            rotate([15, 0, 0]) {
                scale([0.7, 1, 0.5]) sphere(r=6);
            }
        }
    }
}

module eyes() {
    color([0.9, 0.9, 0.9]) {
        // Left eye
        translate([-3, 8, 17.5]) {
            rotate([0, 15, 0]) {
                scale([0.8, 1, 1]) sphere(r=1.5);
            }
        }

        // Right eye
        translate([3, 8, 17.5]) {
            rotate([0, -15, 0]) {
                scale([0.8, 1, 1]) sphere(r=1.5);
            }
        }
    }

    color([0.1, 0.1, 0.5]) {
        // Left pupil
        translate([-3, 8.5, 18]) {
            sphere(r=0.7);
        }

        // Right pupil
        translate([3, 8.5, 18]) {
            sphere(r=0.7);
        }
    }
}

module neck_ruff() {
    color([0.3, 0.7, 0.4]) {
        for(i = [0:30:330]) {
            rotate([0, 0, i]) {
                translate([0, -2, 12]) {
                    rotate([0, 20, 0]) {
                        scale([2.5, 0.5, 3]) cylinder(r1=3, r2=1, h=5);
                    }
                }
            }
        }
    }
}

module neck() {
    color([0.2, 0.6, 0.3]) {
        for(i = [0:10:50]) {
            translate([0, -i*0.8, 15-i*0.3]) {
                scale([1-i*0.01, 1-i*0.01, 1-i*0.01]) {
                    rotate([0, 0, sin(i*3)*5]) {
                        sphere(r=7-i*0.1);
                    }
                }
            }
        }
    }
}

module body_coils() {
    color([0.2, 0.6, 0.3]) {
        // Main body coils
        for(t = [0:5:180]) {
            translate([sin(t)*20, -cos(t)*20-40, 5+t*0.05]) {
                rotate([0, 0, t]) {
                    scale([1-t*0.002, 1-t*0.002, 1-t*0.002]) {
                        sphere(r=6-t*0.02);
                    }
                }
            }
        }
    }
}

module tail() {
    color([0.2, 0.6, 0.3]) {
        // Tail extension
        translate([20, -60, 8]) {
            rotate([0, 10, 45]) {
                scale([0.8, 0.8, 2]) cylinder(r1=4, r2=2, h=15);
            }
        }
    }
}

module leaf_collar() {
    color([0.4, 0.8, 0.5]) {
        // Main collar leaves
        for(i = [0:45:315]) {
            rotate([0, 0, i]) {
                translate([0, -5, 12]) {
                    scale([1, 2, 0.2]) {
                        rotate([0, 30, 0]) {
                            cylinder(r1=4, r2=0, h=6);
                        }
                    }
                }
            }
        }
    }
}

module yellow_markings() {
    color([0.9, 0.8, 0.2]) {
        // Head crest marking
        translate([0, -3, 22]) {
            scale([2, 0.5, 0.3]) sphere(r=3);
        }

        // Neck markings
        for(i = [10:15:40]) {
            translate([0, -i*0.8, 15-i*0.3]) {
                scale([3, 0.5, 0.3]) sphere(r=2);
            }
        }
    }
}

module serperior_model() {
    union() {
        head();
        eyes();
        neck_ruff();
        neck();
        body_coils();
        tail();
        leaf_collar();
        yellow_markings();
    }
}

// Scale for 3D printing (80mm height)
scale([1, 1, 1]) {
    serperior_model();
}

// Tepig realistic-style model for 3D printing
// Color definitions
tepig_orange = [0.96, 0.39, 0.15];
tepig_black = [0.18, 0.18, 0.18];
tepig_yellow = [0.98, 0.87, 0.30];
tepig_pink = [0.96, 0.72, 0.82];

// Scale factor for 3D printing (target ~80mm height)
scale_factor = 80/100;

// Main body
module tepig_body() {
    color(tepig_orange) {
        // Torso
        translate([0, 0, 35*scale_factor])
            scale([1.2*scale_factor, 0.8*scale_factor, 1*scale_factor])
            sphere(25*scale_factor);

        // Belly
        color(tepig_yellow)
        translate([0, -5*scale_factor, 20*scale_factor])
            scale([0.8*scale_factor, 0.4*scale_factor, 0.6*scale_factor])
            sphere(20*scale_factor);
    }
}

// Head
module tepig_head() {
    color(tepig_orange) {
        // Main head shape
        translate([0, 35*scale_factor, 55*scale_factor])
            scale([1.1*scale_factor, 1*scale_factor, 1.1*scale_factor])
            sphere(20*scale_factor);

        // Snout
        translate([0, 50*scale_factor, 52*scale_factor])
            scale([0.8*scale_factor, 0.9*scale_factor, 0.7*scale_factor])
            sphere(15*scale_factor);
    }

    // Nose
    color(tepig_black)
    translate([0, 60*scale_factor, 52*scale_factor])
        scale([0.3*scale_factor, 0.2*scale_factor, 0.2*scale_factor])
        sphere(8*scale_factor);

    // Nostrils
    for(x = [-3, 3]) {
        translate([x*scale_factor, 62*scale_factor, 54*scale_factor])
            sphere(2*scale_factor);
    }
}

// Ears
module tepig_ears() {
    color(tepig_black)
    for(side = [-1, 1]) {
        translate([side*15*scale_factor, 25*scale_factor, 65*scale_factor]) {
            rotate([0, -20, side*20])
                scale([0.3*scale_factor, 0.6*scale_factor, 0.8*scale_factor])
                sphere(10*scale_factor);

            // Inner ear
            color(tepig_pink)
            translate([0, 0, 3*scale_factor])
                scale([0.2*scale_factor, 0.4*scale_factor, 0.6*scale_factor])
                sphere(8*scale_factor);
        }
    }
}

// Eyes
module tepig_eyes() {
    color(tepig_black)
    for(side = [-1, 1]) {
        // Eye shape
        translate([side*12*scale_factor, 40*scale_factor, 58*scale_factor]) {
            scale([0.4*scale_factor, 0.3*scale_factor, 0.15*scale_factor])
                sphere(10*scale_factor);

            // Eye highlight
            color([1,1,1])
            translate([2*scale_factor, 1*scale_factor, 2*scale_factor])
                sphere(2*scale_factor);
        }
    }
}

// Legs
module tepig_legs() {
    color(tepig_orange)
    for(side = [-1, 1]) {
        // Front legs
        translate([side*15*scale_factor, 20*scale_factor, 0]) {
            cylinder(h=25*scale_factor, r=8*scale_factor, $fn=16);
            // Hoof
            color(tepig_black)
            translate([0, 0, 25*scale_factor])
                cylinder(h=5*scale_factor, r=9*scale_factor, $fn=16);
        }

        // Rear legs
        translate([side*12*scale_factor, -20*scale_factor, 0]) {
            cylinder(h=30*scale_factor, r=10*scale_factor, $fn=16);
            // Hoof
            color(tepig_black)
            translate([0, 0, 30*scale_factor])
                cylinder(h=5*scale_factor, r=11*scale_factor, $fn=16);
        }
    }
}

// Tail
module tepig_tail() {
    color(tepig_black)
    translate([0, -30*scale_factor, 25*scale_factor]) {
        // Tail base
        rotate([0, 90, 0])
            cylinder(h=15*scale_factor, r1=8*scale_factor, r2=6*scale_factor, $fn=16);

        // Tail curl
        translate([-15*scale_factor, 0, 0])
            rotate([0, 0, -90])
                scale([1*scale_factor, 1*scale_factor, 0.8*scale_factor])
                difference() {
                    cylinder(h=20*scale_factor, r=7*scale_factor, $fn=16);
                    translate([0, 0, -1])
                        cylinder(h=22*scale_factor, r=4*scale_factor, $fn=16);
                }
    }
}

// Markings on back
module tepig_markings() {
    color(tepig_black)
    for(i = [0:2]) {
        rotate([0, 0, 120*i])
        translate([0, -5*scale_factor, 45*scale_factor])
            scale([0.2*scale_factor, 0.4*scale_factor, 0.1*scale_factor])
            sphere(10*scale_factor);
    }
}

// Assembly
union() {
    tepig_body();
    tepig_head();
    tepig_ears();
    tepig_eyes();
    tepig_legs();
    tepig_tail();
    tepig_markings();
}

// Pignite Pokemon - Realistic Style, Medium Complexity
// Dimensions: ~70mm height, 3D printable

// Main colors
$fn = 32;

module pignite() {
    // Body - main torso
    color([0.85, 0.4, 0.3]) union() {
        // Torso
        translate([0, 0, 25])
            scale([1.2, 1, 1.5])
            sphere(15);

        // Belly
        color([0.9, 0.9, 0.8])
            translate([0, -14.5, 25])
            scale([0.8, 0.3, 1])
            sphere(15);
    }

    // Head
    color([0.85, 0.4, 0.3])
        translate([0, 0, 45])
        scale([0.9, 0.9, 1])
        sphere(12);

    // Snout
    color([0.85, 0.4, 0.3])
        translate([0, 12, 45])
        scale([0.7, 1, 0.6])
        rotate([20, 0, 0])
        sphere(8);

    // Nose
    color([0.2, 0.2, 0.2])
        translate([0, 17, 47])
        sphere(2);

    // Eyes
    color([0.1, 0.1, 0.1])
    union() {
        translate([-5, 8, 48])
            sphere(2.5);
        translate([5, 8, 48])
            sphere(2.5);
    }

    // Eye whites
    color([1, 1, 1])
    union() {
        translate([-5, 8, 49.5])
            scale([0.7, 0.7, 0.3])
            sphere(2.5);
        translate([5, 8, 49.5])
            scale([0.7, 0.7, 0.3])
            sphere(2.5);
    }

    // Ears
    color([0.85, 0.4, 0.3])
    union() {
        translate([-8, 0, 52])
            rotate([0, 0, -30])
            scale([0.4, 0.1, 0.8])
            cylinder(10, 5, 3);
        translate([8, 0, 52])
            rotate([0, 0, 30])
            scale([0.4, 0.1, 0.8])
            cylinder(10, 5, 3);
    }

    // Arms
    color([0.85, 0.4, 0.3])
    union() {
        // Left arm
        translate([-18, 0, 35])
            rotate([0, 0, 20])
            scale([0.6, 0.6, 1.2])
            cylinder(12, 6, 5);

        // Right arm
        translate([18, 0, 35])
            rotate([0, 0, -20])
            scale([0.6, 0.6, 1.2])
            cylinder(12, 6, 5);
    }

    // Hands
    color([0.7, 0.3, 0.2])
    union() {
        translate([-20, 0, 25])
            sphere(6);
        translate([20, 0, 25])
            sphere(6);
    }

    // Legs
    color([0.7, 0.3, 0.2])
    union() {
        translate([-8, -5, 10])
            scale([0.7, 0.7, 0.8])
            cylinder(15, 8, 7);
        translate([8, -5, 10])
            scale([0.7, 0.7, 0.8])
            cylinder(15, 8, 7);
    }

    // Feet
    color([0.7, 0.3, 0.2])
    union() {
        translate([-8, -5, 0])
            scale([1.5, 2, 0.8])
            cube([8, 4, 5], center=true);
        translate([8, -5, 0])
            scale([1.5, 2, 0.8])
            cube([8, 4, 5], center=true);
    }

    // Tail
    color([0.85, 0.4, 0.3])
    translate([0, -18, 20])
        rotate([-30, 0, 0])
        scale([0.8, 0.8, 1.5])
        sphere(10);

    // Tail tip
    color([0.2, 0.2, 0.2])
    translate([0, -25, 15])
        sphere(4);

    // Black stripes pattern
    color([0.2, 0.2, 0.2])
    union() {
        // Body stripes
        translate([0, 0, 35])
            rotate([90, 0, 0])
            scale([1.3, 1.1, 0.5])
            cylinder(8, 16, 16);

        translate([0, 0, 25])
            rotate([90, 0, 0])
            scale([1.3, 1.1, 0.5])
            cylinder(8, 16, 16);

        // Arm stripes
        translate([-18, 0, 30])
            rotate([0, 0, 20])
            rotate([90, 0, 0])
            scale([0.7, 0.7, 0.5])
            cylinder(4, 6, 6);

        translate([18, 0, 30])
            rotate([0, 0, -20])
            rotate([90, 0, 0])
            scale([0.7, 0.7, 0.5])
            cylinder(4, 6, 6);
    }
}

// Generate the complete model
pignite();

// Emboar - Realistic Medium Complexity
// Scale: 1 unit = 1mm, final size ~70mm tall

module emboar() {
    color([0.8,0.2,0.1]) {
        // Body - main torso
        union() {
            translate([0,0,25]) 
                scale([1.2,1,1.5]) 
                    sphere(15, $fn=32);

            // Chest armor
            difference() {
                translate([0,8,30])
                    scale([1.3,0.8,1.2])
                        sphere(12, $fn=32);
                translate([0,8,35])
                    scale([1.2,0.7,1])
                        sphere(11, $fn=32);
            }

            // Belly pattern
            color([0.9,0.9,0.8])
                translate([0,10,25])
                    scale([0.8,0.3,0.6])
                        sphere(8, $fn=32);
        }

        // Head
        translate([0,20,45]) {
            // Main head shape
            scale([0.9,1.2,1.1])
                sphere(12, $fn=32);

            // Snout
            translate([0,12,0]) {
                rotate([90,0,0])
                    cylinder(h=15, r1=8, r2=6, $fn=16);

                // Nose
                color([0.1,0.1,0.1])
                    translate([0,7,0])
                        sphere(2, $fn=16);
            }

            // Eyes
            color([0.1,0.1,0.1])
            for(x=[-5,5]) {
                translate([x,8,3])
                    sphere(2, $fn=16);
            }

            // Ears
            color([0.7,0.15,0.1])
            for(x=[-8,8]) {
                translate([x,0,8])
                    rotate([0,20*x,0])
                        scale([0.3,1,1.5])
                            sphere(4, $fn=16);
            }

            // Head flame collar
            color([1,0.5,0])
            for(a=[0:45:315]) {
                translate([0,0,0])
                    rotate([0,0,a])
                        translate([0,12,6])
                            scale([0.3,0.3,1.5])
                                rotate([45,0,0])
                                    cylinder(h=8, r1=2, r2=0.1, $fn=8);
            }
        }

        // Arms
        module arm(side) {
            translate([side*12,10,35]) {
                // Upper arm
                rotate([0,0,side*20])
                    cylinder(h=15, r=5, $fn=16);

                // Elbow armor
                translate([0,0,15])
                    sphere(6, $fn=16);

                // Lower arm
                translate([0,-3,15])
                    rotate([0,side*30,0])
                        cylinder(h=12, r=4, $fn=16);

                // Hand
                translate([0,-6,25])
                    rotate([0,side*45,0])
                        scale([1,1.2,1])
                            sphere(5, $fn=16);

                // Wrist bands
                color([0.8,0.8,0.8])
                translate([0,-3,22])
                    rotate([0,side*30,0])
                        scale([1.2,1.2,0.5])
                            cylinder(h=3, r=4.5, $fn=16);
            }
        }

        arm(1);
        arm(-1);

        // Legs
        module leg(side) {
            translate([side*8,-8,15]) {
                // Thigh
                rotate([0,0,side*-10])
                    cylinder(h=15, r=6, $fn=16);

                // Knee
                translate([0,0,15])
                    sphere(7, $fn=16);

                // Lower leg
                translate([0,-5,15])
                    rotate([0,side*-20,0])
                        cylinder(h=15, r=5, $fn=16);

                // Foot
                translate([0,-8,28])
                    scale([1.5,2,0.8])
                        sphere(5, $fn=16);

                // Ankle bands
                color([0.8,0.8,0.8])
                translate([0,-5,25])
                    rotate([0,side*-20,0])
                        scale([1.2,1.2,0.5])
                            cylinder(h=3, r=5.5, $fn=16);
            }
        }

        leg(1);
        leg(-1);

        // Tail
        translate([0,-15,20]) {
            rotate([0,30,0])
                cylinder(h=20, r1=6, r2=3, $fn=16);

            // Tail flame
            color([1,0.5,0])
            for(a=[0:60:300]) {
                rotate([a-30,0,0])
                    translate([0,0,20])
                        scale([0.3,0.3,1])
                            cylinder(h=8, r1=2, r2=0.1, $fn=8);
            }
        }

        // Shoulder armor
        color([0.8,0.8,0.8])
        for(x=[-12,12]) {
            translate([x,12,35])
                rotate([0,0,x*-5])
                    scale([1.2,1,0.8])
                        sphere(6, $fn=16);
        }
    }
}

emboar();

// Oshawott realistic style - 3D printable
// Overall height ~70mm

$fn = 36;

module oshawott() {
    color("LightBlue") head();
    color("LightBlue") body();
    color("LightBlue") arms();
    color("LightBlue") legs();
    color("LightBlue") tail();
    color("Tan") feet();
    color("Tan") nose();
    color("White") face_disc();
    color("Black") eyes();
    color("DarkRed") mouth();
    color("Black") ears();
    color("Tan") scalchop();
}

module head() {
    translate([0, 0, 55])
    sphere(d = 25);
}

module body() {
    translate([0, 0, 30])
    sphere(d = 30);
}

module arms() {
    // Left arm
    translate([-15, 10, 40])
    rotate([0, 0, 20])
    cylinder(h = 20, d1 = 8, d2 = 6, center = true);

    // Right arm
    mirror([1, 0, 0])
    translate([-15, 10, 40])
    rotate([0, 0, 20])
    cylinder(h = 20, d1 = 8, d2 = 6, center = true);
}

module legs() {
    // Left leg
    translate([-8, 0, 15])
    cylinder(h = 15, d = 10);

    // Right leg
    translate([8, 0, 15])
    cylinder(h = 15, d = 10);
}

module tail() {
    translate([0, -15, 25])
    rotate([20, 0, 0])
    cylinder(h = 25, d1 = 12, d2 = 6, center = true);
}

module feet() {
