// Pansage Pokemon - Realistic Style - Medium Complexity
// Based on the grass-type monkey Pokemon

// Overall dimensions
ear_height = 15;
head_radius = 12;
body_height = 20;
body_width = 18;
body_depth = 15;
arm_length = 12;
leg_length = 10;
tail_length = 25;
tail_width = 6;

// Colors
$fn = 32;

module head() {
    color([0.4, 0.8, 0.3]) union() {
        // Main head sphere
        translate([0, 0, head_radius]) sphere(r=head_radius);

        // Ears - three leaf-like ears on top
        for(i=[-1, 0, 1]) {
            translate([i * 10, 0, head_radius + 5]) 
                rotate([0, i * 10, 0])
                rotate([0, 0, 45 * i])
                scale([0.8, 0.3, 1])
                cylinder(h=ear_height, r1=ear_height/2, r2=0);
        }

        // Face details
        // Eyes
        color([0.9, 0.9, 0.9]) {
            translate([-5, head_radius - 2, 5]) sphere(r=2.5);
            translate([5, head_radius - 2, 5]) sphere(r=2.5);
        }

        color([0, 0, 0]) {
            translate([-5, head_radius - 1, 5.5]) sphere(r=1.5);
            translate([5, head_radius - 1, 5.5]) sphere(r=1.5);
        }

        // Nose
        color([0.3, 0.6, 0.2]) 
            translate([0, head_radius - 1, 2]) sphere(r=1.5);

        // Mouth
        color([0.8, 0.2, 0.2]) 
            translate([0, head_radius - 1, -2]) 
            scale([2, 0.5, 0.5])
            sphere(r=1);

        // Leaf collar around neck
        color([0.3, 0.7, 0.2]) 
            translate([0, 0, -head_radius/2]) 
            rotate_extrude(angle=360) 
            translate([head_radius + 2, 0, 0]) 
            circle(r=2);
    }
}

module body() {
    color([0.4, 0.8, 0.3]) union() {
        // Main body
        translate([0, 0, -body_height/2]) 
            scale([1, 0.8, 1])
            cube([body_width, body_depth, body_height], center=true);

        // Belly marking
        color([0.9, 1, 0.9]) 
            translate([0, body_depth/2 - 1, -body_height/4]) 
            scale([0.6, 0.1, 0.6])
            cube([body_width, 2, body_height], center=true);

        // Arms
        for(side=[-1, 1]) {
            translate([side * (body_width/2 + arm_length/3), 0, body_height/4]) {
                rotate([0, 30, side * 20])
                color([0.35, 0.75, 0.25]) 
                    cylinder(h=arm_length, r=arm_length/4, center=true);

                // Hands
                translate([0, 0, arm_length/2 + arm_length/4]) 
                    sphere(r=arm_length/3);
            }
        }

        // Legs
        for(side=[-1, 1]) {
            translate([side * body_width/3, 0, -body_height/2 - leg_length/2]) {
                color([0.35, 0.75, 0.25]) 
                    cylinder(h=leg_length, r=leg_length/3);

                // Feet
                translate([0, body_depth/4, -leg_length/2]) 
                    rotate([90, 0, 0])
                    cylinder(h=leg_length/2, r1=leg_length/2, r2=leg_length/3);
            }
        }
    }
}

module tail() {
    color([0.3, 0.7, 0.2]) union() {
        // Main tail
        translate([0, body_depth/2, -body_height/2]) {
            rotate([0, -30, 0])
            cylinder(h=tail_length, r=tail_width/2);

            // Leaf at end of tail
            translate([0, 0, -tail_length - 2]) 
                rotate([0, 90, 0])
                rotate([0, 0, 45])
                scale([1.5, 0.2, 1])
                cylinder(h=tail_width*2, r1=tail_width, r2=0);
        }
    }
}

// Assembly
union() {
