        }
    }
}

// Wing module
module wing(side) {
    mirror([side, 0, 0]) {
        color($bodyColor) {
            // Main wing
            translate([-15, -5, 10]) rotate([0, -15, 0]) 
                difference() {
                    scale([1.5, 0.5, 1]) sphere(d=20);
                    translate([0, 0, -15]) cube([30, 20, 30], center=true);
                }

            // Wing tip
            translate([-25, -5, 15]) rotate([0, -30, -30]) 
                scale([1, 0.3, 0.7]) sphere(d=12);

            // Wing markings
            color($accentColor) {
                for(i = [0, 1, 2]) {
                    translate([-20 + i*5, -8, 10 + i*3]) 
                        rotate([0, 0, 30]) cube([3, 0.5, 8]);
                }
            }
        }
    }
}

// Tail module
module tail() {
    color($bodyColor) {
        // Main tail feathers
        for(i = [-1, 0, 1]) {
            translate([0, -20, 5 + i*5]) rotate([0, -20, i*15]) {
                scale([0.8, 0.2, 1.2]) sphere(d=15);
                color($detailColor) 
                    translate([0, -8, 0]) cube([10, 0.5, 2]);
            }
        }
    }
}

// Legs and feet
module feet() {
    color($beakColor) {
        for(side = [-1, 1]) {
            translate([side*5, 5, -15]) {
                // Leg
                cylinder(h=8, d=3);

                // Foot
                translate([0, 0, -10]) {
                    rotate([90, 0, 0]) cylinder(h=8, d=3);
                    translate([side*3, 0, 0]) rotate([90, 0, 0]) cylinder(h=4, d=3);
                }
            }
        }
    }
}

// Main assembly
union() {
    body();
    head();
    wing(1);   // Right wing
    wing(-1);  // Left wing
    tail();
    feet();

    // Ground base for stability
    color($accentColor) {
        translate([0, 0, -20]) cylinder(h=2, d=30);
    }
}

