color_yellow = [1, 0.8, 0];
color_orange = [1, 0.5, 0];

// Main body module
module roggenrola() {
    // Central rock body
    color(color_body)
    difference() {
        sphere(d = 45);

        // Create hexagonal rock shape
        for(i = [0:5]) {
            rotate([0, 0, i * 60])
            translate([0, 35, 0])
            cube([70, 20, 45], center = true);
        }
    }

    // Face area
    color(color_face)
    translate([0, 0, 25])
    sphere(d = 30);

    // Eyes
    module eye(x_pos) {
        color(color_eye)
        translate([x_pos, 5, 32])
        sphere(d = 8);

        color(color_pupil)
        translate([x_pos, 8, 34])
        sphere(d = 4);
    }

    eye(-8);
    eye(8);

    // Mouth
    color(color_pupil)
    translate([0, -8, 25])
    rotate([90, 0, 0])
    cylinder(h = 2, d1 = 12, d2 = 8, $fn = 16);

    // Ears/rock protrusions
    module ear(x_pos, z_rot) {
        color(color_body)
        translate([x_pos, 20, 5])
        rotate([0, 0, z_rot])
        difference() {
            cube([12, 20, 8], center = true);
            translate([0, 10, 0])
            cube([14, 22, 10], center = true);
        }
    }

    ear(-18, -20);
    ear(18, 20);

    // Rock segments around body
    for(i = [0:11]) {
        angle = i * 30;
        color(color_body)
        translate([20 * cos(angle), 20 * sin(angle), 0])
        rotate([0, 0, angle + 15])
        difference() {
            cube([10, 15, 8], center = true);
            translate([0, 8, 0])
            cube([12, 17, 10], center = true);
        }
    }

    // Yellow ore veins
    for(i = [0:2]) {
        angle = i * 120 + 45;
        color(color_yellow)
        translate([15 * cos(angle), 15 * sin(angle), 10])
        rotate([90, 0, angle])
        cylinder(h = 20, d = 3, $fn = 8);
    }

    // Orange ore spots
    for(i = [0:4]) {
        angle = i * 72 + 15;
        color(color_orange)
        translate([18 * cos(angle), 18 * sin(angle), -5])
        sphere(d = 4);
    }

    // Bottom flat base for printing
    color(color_body)
    translate([0, 0, -22])
    cylinder(h = 5, d = 40, $fn = 32);
}

