eye_color = [0.95, 0.95, 0.10];    // yellow
pupil_color = [0, 0, 0];           // black
spot_color = [0.05, 0.05, 0.05];   // dark gray

// Main body
module body() {
    difference() {
        // Central blob
        color(body_color)
        scale([1, 0.9, 0.7])
        sphere(30 * scale_factor, $fn = 50);

        // Flatten top slightly
        translate([0, 0, 15 * scale_factor])
        cube([60 * scale_factor, 60 * scale_factor, 30 * scale_factor], center = true);
    }
}

// Belly patch
module belly() {
    color(belly_color)
    translate([0, 0, -19 * scale_factor])
    scale([0.9, 1, 0.3])
    sphere(25 * scale_factor, $fn = 40);
}

// Eye
module eye(pos) {
    translate(pos) {
        // Eyeball
        color(eye_color)
        sphere(6 * scale_factor, $fn = 20);

        // Pupil
        color(pupil_color)
        translate([0, 0, 5.5 * scale_factor])
        scale([1, 1, 0.3])
        sphere(3 * scale_factor, $fn = 15);
    }
}

// Eyebrow ridge
module eyebrow(pos) {
    translate(pos) rotate([0, 0, 15 * (pos[0] < 0 ? 1 : -1)]) {
        color(body_color)
        rotate([90, 0, 0])
        cylinder(h = 12 * scale_factor, r1 = 3 * scale_factor, r2 = 1 * scale_factor, $fn = 10);
    }
}

// Nose bump (two small spheres)
module nose() {
    translate([-4 * scale_factor, 25 * scale_factor, 5 * scale_factor])
    color(body_color)
    sphere(3 * scale_factor, $fn = 10);

    translate([4 * scale_factor, 25 * scale_factor, 5 * scale_factor])
    color(body_color)
    sphere(3 * scale_factor, $fn = 10);
}

// Mouth
module mouth() {
    translate([0, 26 * scale_factor, -4 * scale_factor])
    rotate([90, 0, 0])
    color([0.1, 0.1, 0.1])
    cylinder(h = 1 * scale_factor, r = 8 * scale_factor, $fn = 20);
}

// Ear
module ear(pos, angle = 0) {
    translate(pos) rotate([0, 0, angle]) {
        // Main ear bulb
        color(body_color)
        scale([0.8, 1.2, 1])
        sphere(8 * scale_factor, $fn = 20);

        // Inner ear
        translate([0, 1 * scale_factor, 0])
        color(belly_color)
        scale([0.6, 1, 0.8])
        sphere(6 * scale_factor, $fn = 15);
    }
}

// Vocal sac bubble
module bubble(pos) {
    translate(pos) {
        color(body_color)
        sphere(5 * scale_factor, $fn = 15);
    }
}

// Leg
module leg(pos, angle = 0) {
    translate(pos) rotate([0, 0, angle]) {
        // Upper leg
        color(body_color)
        rotate([90, 0, 0])
        cylinder(h = 12 * scale_factor, r1 = 5 * scale_factor, r2 = 4 * scale_factor, $fn = 10);

        // Lower leg
        translate([0, 0, -14 * scale_factor])
        cylinder(h = 10 * scale_factor, r1 = 4 * scale_factor, r2 = 5 * scale_factor, $fn = 10);

        // Foot
        translate([0, 0, -26 * scale_factor])
        color(body_color)
        scale([1.5, 2, 0.7])
        sphere(5 * scale_factor, $fn = 10);
    }
}

// Tail
module tail() {
    translate([0, -28 * scale_factor, -5 * scale_factor])
    rotate([20, 0, 0])
    color(body_color)
    scale([1, 0.6, 0.6])
    sphere(12 * scale_factor, $fn = 20);
}

// Dark spots on back
module spots() {
    for (i = [-1:1]) {
        translate([i * 10 * scale_factor, -5 * scale_factor, 15 * scale_factor])
        color(spot_color)
        sphere(3 * scale_factor, $fn = 8);
    }
    for (i = [-1:1]) {
        translate([i * 8 * scale_factor, -15 * scale_factor, 12 * scale_factor])
        color(spot_color)
        sphere(2.5 * scale_factor, $fn = 8);
    }
}

// Assembly
union() {
    body();
    belly();
    tail();

    // Eyes
    eye([-12 * scale_factor, 20 * scale_factor, 8 * scale_factor]);
    eye([12 * scale_factor, 20 * scale_factor, 8 * scale_factor]);

    // Eyebrows
    eyebrow([-15 * scale_factor, 22 * scale_factor, 15 * scale_factor]);
    eyebrow([15 * scale_factor, 22 * scale_factor, 15 * scale_factor]);

    // Nose and mouth
    nose();
    mouth();

    // Ears
    ear([-20 * scale_factor, 5 * scale_factor, 18 * scale_factor], -30);
    ear([20 * scale_factor, 5 * scale_factor, 18 * scale_factor], 30);

    // Vocal sac bubbles
    for (a = [0:60:300]) {
        rotate([0, 0, a])
        translate([0, 28 * scale_factor, -10 * scale_factor])
        bubble([0, 0, 0]);
    }

    // Legs
    leg([-15 * scale_factor, 0, -20 * scale_factor], -20);
    leg([15 * scale_factor, 0, -20 * scale_factor], 20);
    leg([-10 * scale_factor, -20 * scale_factor, -20 * scale_factor], -10);
    leg([10 * scale_factor, -20 * scale_factor, -20 * scale_factor], 10);

    // Spots
    spots();
}

