module swadloon() {
    // Main cloak/leaf wrapping
    color([0.2, 0.4, 0.1]) {
        // Front cloak leaf
        translate([0, -body_depth/2, 0])
        rotate([90, 0, 0])
        difference() {
            cylinder(h=body_height*1.2, r1=body_width/2, r2=body_width/1.5);
            translate([0, 0, -cloak_thickness])
            cylinder(h=body_height*1.2+cloak_thickness*2, r1=body_width/2-cloak_thickness, r2=body_width/1.5-cloak_thickness);
        }

        // Side cloak leaf
        rotate([0, 0, 45])
        translate([0, -body_depth/2, 0])
        rotate([90, 0, 0])
        difference() {
            cylinder(h=body_height*0.8, r1=body_width/2.5, r2=body_width/3);
            translate([0, 0, -cloak_thickness])
            cylinder(h=body_height*0.8+cloak_thickness*2, r1=body_width/2.5-cloak_thickness, r2=body_width/3-cloak_thickness);
        }

        // Other side cloak leaf
        rotate([0, 0, -45])
        translate([0, -body_depth/2, 0])
        rotate([90, 0, 0])
        difference() {
            cylinder(h=body_height*0.8, r1=body_width/2.5, r2=body_width/3);
            translate([0, 0, -cloak_thickness])
            cylinder(h=body_height*0.8+cloak_thickness*2, r1=body_width/2.5-cloak_thickness, r2=body_width/3-cloak_thickness);
        }
    }

    // Body inside cloak
    color([0.4, 0.3, 0.2]) {
        translate([0, 0, body_height/3])
        sphere(d=body_width*0.8);
    }

    // Head
