        rotate([30, 20, 30])
        scale([0.3, 0.8, 0.1])
        sphere(d=body_width/2);
    }

    // Eyes
    color([0.9, 0.9, 0.9]) {
        // Left eye
        translate([-body_width/6, body_depth/2.5, body_height*1.5])
        sphere(d=body_width/8);

        // Right eye
        translate([body_width/6, body_depth/2.5, body_height*1.5])
        sphere(d=body_width/8);
