// Scolipede - Realistic Style
// Medium complexity 3D printable model

// Color definitions
color_body = [139/255, 0/255, 0/255];      // Dark red
color_accents = [128/255, 128/255, 128/255]; // Gray
color_eyes = [255/255, 255/255, 0/255];   // Yellow
color_teeth = [255/255, 255/255, 255/255]; // White

// Main body segments
module body_segment(radius, length, is_head=false) {
    union() {
        // Main segment body
        color(color_body)
        cylinder(h=length, r1=radius, r2=radius*0.8, $fn=16);

        // Segment rings
        for(i=[0:3]) {
            translate([0, 0, i*length/4])
            color(color_accents)
            cylinder(h=1, r=radius*1.05, $fn=16);
        }

        // Purple markings
        if(!is_head) {
            for(i=[0:2]) {
                rotate([0, 0, i*120])
                translate([radius*0.7, 0, length/2])
                color([75/255, 0/255, 130/255])
                cube([radius*0.4, 1, length*0.4], center=true);
            }
        }
    }
}

// Horn module
module horn() {
    union() {
        // Base
        color(color_accents)
        cylinder(h=5, r1=4, r2=2, $fn=8);

        // Tip
        translate([0, 0, 5])
        color(color_accents)
        cone(h=8, r1=2, r2=0.5, $fn=8);
    }
}

// Leg module
module leg(length, angle) {
    rotate([0, 0, angle])
    translate([0, -3, 0])
    union() {
        // Upper leg
        color(color_body)
        rotate([90, 0, 0])
        cylinder(h=length, r=2, $fn=8);

        // Lower leg
        translate([0, 0, length])
        rotate([45, 0, 0])
        color(color_body)
        cylinder(h=length*0.8, r=1.5, $fn=8);

        // Foot
        translate([0, 0, length + length*0.8*sin(45)])
        rotate([90, 0, 0])
        color(color_accents)
        cylinder(h=3, r1=2, r2=3, $fn=8);
    }
}

// Eye module
module eye() {
    union() {
        // Eye socket
        color([0, 0, 0])
        sphere(r=3, $fn=16);

        // Pupil
        translate([0, 0.5, 1])
        color(color_eyes)
        sphere(r=2, $fn=16);
    }
}

// Mandibles
module mandible() {
    color(color_accents)
    difference() {
        union() {
            // Main mandible
            rotate([0, -30, 0])
            cube([8, 2, 3]);

            // Teeth
            for(i=[0:3]) {
                translate([i*2 - 3, 0, 1.5])
                color(color_teeth)
                cube([0.5, 2, 1]);
            }
        }
        // Hollow for printing
        translate([2, 0, 0])
        cube([6, 1.8, 2.5], center=true);
    }
}

