    color("black") {
        translate([0, -12, 15])
            rotate([90, 0, 0])
                cylinder(d1=8, d2=12, h=3, $fn=20);
    }

    // Ears
    module ear(side) {
        translate([side * 20, 0, 15])
            rotate([0, 0, side * 15])
                color("orange") {
                    // Main ear shape
                    scale([0.6, 1, 1.2])
                        sphere(d=20, $fn=20);

                    // Inner ear
                    color("beige") {
                        translate([0, -2, 2])
                            scale([0.4, 0.7, 0.8])
                                sphere(d=15, $fn=15);
                    }
                }
    }

    ear(1);
    ear(-1);

    // Arms
    module arm(side) {
        translate([side * 20, -5, -5])
            rotate([0, 0, side * -20])
                color("orange") {
                    // Upper arm
                    cylinder(d1=10, d2=8, h=15, $fn=15);

                    // Hand
                    translate([0, -8, 8])
                        sphere(d=12, $fn=15);

                    // Fingers
                    for(i = [0:1:2]) {
                        rotate([0, 0, (i-1) * 30])
                            translate([0, -12, 5])
                                cylinder(d=4, h=6, $fn=8);
                    }
                }
    }

    arm(1);
    arm(-1);

    // Feet
    module foot(side) {
        translate([side * 12, -15, -25])
            color("orange") {
                // Main foot
                scale([1.2, 1.5, 0.8])
                    sphere(d=15, $fn=15);

                // Toes
                for(i = [0:1:2]) {
                    translate([i * 4 - 4, -8, -2])
                        sphere(d=5, $fn=8);
                }
            }
    }

    foot(1);
    foot(-1);

    // Belly swirl pattern
    color("yellow") {
        translate([0, -10, 0])
            rotate([90, 0, 0])
                linear_extrude(height=1, center=true)
                    circle(d=15, $fn=3);
    }

    // Back stripe pattern
    color("darkorange") {
        // Three vertical stripes
        for(i = [-1, 0, 1]) {
            translate([i * 6, 0, 0])
                cube([2, 3, 40], center=true);
        }
    }
}

