// Dwebble - OpenSCAD 3D Printable Model
// Realistic style, medium complexity

// Parameters
$fn = 32;
scale_factor = 0.8;
body_size = 50 * scale_factor;
shell_radius = body_size * 0.7;
leg_length = body_size * 0.4;
leg_radius = body_size * 0.08;
eye_size = body_size * 0.15;

// Colors
color_body = [0.8, 0.5, 0.3];
color_shell = [0.6, 0.4, 0.2];
color_eye = [0.9, 0.9, 0.1];
color_pupil = [0, 0, 0];
color_highlight = [1, 1, 1];

module dwebble() {
    union() {
        // Shell (rocky outer covering)
        color(color_shell)
        translate([0, 0, shell_radius * 0.3])
        scale([1.2, 1, 0.8])
        sphere(r = shell_radius);

        // Shell texture layers
        for(i = [1:3]) {
            color(color_shell)
            translate([0, 0, shell_radius * 0.3 + i * 2])
            scale([1.2 - i * 0.1, 1 - i * 0.05, 0.8 - i * 0.05])
            sphere(r = shell_radius - i * 3);
        }

        // Main body
        color(color_body)
        translate([0, 0, leg_length + body_size * 0.3])
        scale([1, 0.8, 0.6])
        sphere(r = body_size * 0.35);

        // Head
        color(color_body)
        translate([0, -body_size * 0.2, leg_length + body_size * 0.6])
        scale([0.8, 0.7, 0.7])
        sphere(r = body_size * 0.25);

        // Eyes
        for(side = [-1, 1]) {
            color(color_eye)
            translate([side * body_size * 0.15, -body_size * 0.35, leg_length + body_size * 0.7])
            sphere(r = eye_size);

            color(color_pupil)
            translate([side * body_size * 0.15, -body_size * 0.4, leg_length + body_size * 0.75])
            sphere(r = eye_size * 0.5);

            color(color_highlight)
            translate([side * body_size * 0.13, -body_size * 0.42, leg_length + body_size * 0.78])
            sphere(r = eye_size * 0.2);
        }

        // Mouth (small curved opening)
        color([0.2, 0.1, 0.1])
        translate([0, -body_size * 0.45, leg_length + body_size * 0.55])
        rotate([0, 0, 180])
        cylinder(h = 0.5, r1 = body_size * 0.1, r2 = body_size * 0.05);

        // Legs (four legs)
        for(angle = [45, 135, 225, 315]) {
            rotate([0, 0, angle])
            translate([body_size * 0.3, 0, 0])
            leg();
        }

        // Claws (front two legs have larger claws)
        for(side = [-1, 1]) {
            color(color_body)
            translate([side * body_size * 0.4, -body_size * 0.3, leg_length * 0.7])
            rotate([0, 0, side * 30])
            claw();
        }
    }
}

module leg() {
    color(color_body)
    union() {
        // Upper leg segment
        translate([body_size * 0.1, 0, leg_length * 0.8])
        rotate([90, 0, 0])
        cylinder(h = leg_length * 0.6, r = leg_radius);

        // Lower leg segment
        translate([body_size * 0.15, 0, leg_length * 0.4])
        rotate([90, 0, 0])
        cylinder(h = leg_length * 0.5, r = leg_radius * 0.8);

        // Foot
        translate([body_size * 0.2, 0, leg_length * 0.1])
        sphere(r = leg_radius * 1.2);
    }
}

module claw() {
    // Three-pronged claw
    for(i = [0:2]) {
        rotate([0, 0, i * 120])
        translate([body_size * 0.05, 0, 0])
        scale([1, 0.3, 0.3])
        rotate([0, 90, 0])
        cylinder(h = body_size * 0.12, r1 = body_size * 0.03, r2 = body_size * 0.01);
    }
}

