body_color = [0.9, 0.7, 0.5];
eye_color = [0.2, 0.2, 0.8];
claw_color = [0.7, 0.6, 0.5];

// Main module
module crustle() {
    // Position the whole model
    translate([0, 0, 0]) {
        // Body with shell
        union() {
            // Large rock shell
            color(shell_color) {
                translate([0, 0, 15]) rotate([0, 0, 45]) {
                    difference() {
                        // Main shell body
                        scale([1.2, 1, 0.8]) sphere(d=50);

                        // Shell cavity
                        translate([0, 0, 20]) scale([0.9, 0.9, 0.6]) sphere(d=45);

                        // Shell opening
                        translate([0, 0, -5]) cube([30, 30, 50], center=true);
                    }

                    // Shell segments
                    for(i = [0:5]) {
                        rotate([0, 0, i * 60]) {
                            translate([0, 25, 5]) cube([5, 10, 15], center=true);
                        }
                    }
                }
            }

            // Body underneath shell
            color(body_color) {
                // Main body
                translate([0, 0, 5]) scale([0.8, 1, 0.6]) sphere(d=30);

                // Head
                translate([0, -25, 15]) rotate([30, 0, 0]) {
                    sphere(d=15);

                    // Eyes
                    color(eye_color) {
                        translate([-5, -7, 4]) sphere(d=3);
                        translate([5, -7, 4]) sphere(d=3);
                    }

                    // Mandibles
                    color(body_color) {
                        translate([-8, -10, -2]) rotate([0, 30, 0]) cylinder(h=8, r1=3, r2=1);
                        translate([8, -10, -2]) rotate([0, -30, 0]) cylinder(h=8, r1=3, r2=1);
                    }
                }

                // Legs
                for(side = [-1, 1]) {
                    // Front legs
                    translate([side * 15, -10, 0]) {
                        rotate([0, 0, side * 30]) {
                            color(claw_color) {
                                cylinder(h=12, r=3);
                                translate([0, 0, -12]) {
                                    rotate([0, 0, side * 45]) {
                                        scale([1, 0.5, 0.5]) sphere(d=8);
                                    }
                                }
                            }
                        }
                    }

                    // Middle legs
                    translate([side * 20, 0, 0]) {
                        rotate([0, 0, side * 20]) {
                            color(body_color) {
                                cylinder(h=10, r=2.5);
                                translate([0, 0, -10]) {
                                    rotate([0, 0, side * 30]) {
                                        scale([0.8, 0.4, 0.4]) sphere(d=6);
                                    }
                                }
                            }
                        }
                    }

                    // Back legs
                    translate([side * 15, 15, 0]) {
                        rotate([0, 0, side * 15]) {
                            color(body_color) {
                                cylinder(h=8, r=2);
                                translate([0, 0, -8]) {
                                    rotate([0, 0, side * 20]) {
                                        scale([0.6, 0.3, 0.3]) sphere(d=5);
                                    }
                                }
                            }
                        }
                    }
                }

                // Tail spike
                color(rock_color) {
                    translate([0, 20, 10]) rotate([0, 0, 0]) {
                        cylinder(h=15, r1=5, r2=2);
                    }
                }
            }

            // Small rock details on shell
            color(rock_color) {
                for(i = [1:8]) {
                    angle = i * 45;
                    radius = 20;
                    translate([cos(angle) * radius, sin(angle) * radius, 25]) {
                        sphere(d=5 + (i % 3) * 2);
                    }
                }
            }
        }
    }
}

