module tirtouga() {
    scale([scale_factor, scale_factor, scale_factor]) {
        // Main shell (back)
        color(shell_color) {
            difference() {
                // Main shell dome
                translate([0, 0, 8]) {
                    scale([1.2, 1.0, 0.6]) {
                        sphere(8);
                    }
                }

                // Shell pattern grooves
                for(i = [0:3]) {
                    rotate([0, 0, i * 90]) {
                        translate([-2, 0, 4]) {
                            cube([4, 0.5, 3], center=true);
                        }
                    }
                }
            }

            // Shell segments
            for(i = [0:5]) {
                rotate([0, 0, i * 60]) {
                    translate([6, 0, 4]) {
                        scale([1.5, 0.8, 0.4]) {
                            sphere(2);
                        }
                    }
                }
            }
        }

        // Main body
        color(body_color) {
            // Body oval
            translate([0, 0, 4]) {
                scale([1.0, 0.8, 0.6]) {
                    sphere(6);
                }
            }

            // Head
            translate([0, -8, 6]) {
                scale([0.8, 0.9, 0.7]) {
                    sphere(5);
                }
            }

            // Neck connection
            translate([0, -4, 5]) {
                cylinder(h=4, r1=3, r2=2.5, center=true);
            }

            // Front flippers
            for(side = [-1, 1]) {
                translate([side * 4, -2, 2]) {
                    rotate([0, side * 30, 0]) {
                        scale([0.3, 1.2, 0.2]) {
                            sphere(3);
                        }
                    }
                }
            }

            // Rear flippers
            for(side = [-1, 1]) {
                translate([side * 3, 6, 2]) {
                    rotate([0, -side * 20, 0]) {
                        scale([0.4, 1.0, 0.2]) {
                            sphere(2.5);
                        }
                    }
                }
            }

            // Tail
            translate([0, 10, 3]) {
                rotate([0, 0, 180]) {
                    scale([0.5, 1.0, 0.3]) {
                        cylinder(h=4, r1=2, r2=1);
                    }
                }
            }
        }

        // Eyes
        color([0, 0, 0]) {
            for(side = [-1, 1]) {
                translate([side * 1.5, -9, 7]) {
                    sphere(0.8);
                }
            }
        }

        // Eye highlights
        color([1, 1, 1]) {
            for(side = [-1, 1]) {
                translate([side * 1.7, -9.2, 7.2]) {
                    sphere(0.3);
                }
            }
        }

        // Mouth
        color([0.2, 0.1, 0.1]) {
            translate([0, -10, 5]) {
                rotate([90, 0, 0]) {
                    scale([1, 0.5, 1]) {
                        cylinder(h=0.5, r=1);
                    }
                }
            }
        }

        // Shell edge ridge
        color(shell_color) {
            difference() {
                translate([0, 0, 3]) {
                    scale([1.3, 1.1, 0.3]) {
                        sphere(8);
                    }
                }
                translate([0, 0, 3]) {
                    scale([1.2, 1.0, 0.4]) {
                        sphere(7.5);
                    }
                }
            }
        }

        // Belly plate
        color([0.9, 0.8, 0.7]) {
            translate([0, 0, 1]) {
                scale([0.8, 0.7, 0.2]) {
                    sphere(5);
                }
            }
        }
    }
}

