// Carracosta OpenSCAD Model - Realistic Style, Medium Complexity
// Dimensions: ~80mm length

module shell_segment(x, y, z, rx, ry, rz, w, h, d) {
    translate([x, y, z]) rotate([rx, ry, rz]) 
    scale([w, h, d]) sphere(5, $fn=20);
}

module carracosta() {
    color([0.15, 0.35, 0.55]) { // Main shell color
        // Main shell body
        union() {
            scale([1, 0.8, 0.6]) sphere(20, $fn=30);

            // Back shell segments
            for(i = [-3:3]) {
                shell_segment(i * 8, 0, 5, 0, 0, 0, 1, 1.2, 0.8);
            }
            for(i = [-2:2]) {
                shell_segment(i * 10, 0, 12, 0, 0, 0, 0.8, 1, 0.6);
            }
        }
    }

    color([0.1, 0.2, 0.3]) { // Darker shell ridges
        // Ridge details
        for(i = [-15:5:15]) {
            translate([i, 0, 15]) rotate([90, 0, 0]) 
            cylinder(h=2, r1=8, r2=4, $fn=6);
        }
    }

    color([0.3, 0.5, 0.7]) { // Head
        translate([25, 0, 10]) rotate([0, 0, -10]) 
        scale([1.2, 1, 0.8]) sphere(12, $fn=25);
    }

    color([0.3, 0.5, 0.7]) { // Neck
        translate([15, 0, 8]) rotate([0, 0, -15])
        cylinder(h=12, r1=8, r2=6, $fn=20);
    }

    color([0.2, 0.4, 0.6]) { // Flippers
        // Front flippers
        translate([10, 18, -5]) rotate([0, -30, 45])
        scale([0.8, 0.3, 2]) sphere(8, $fn=15);
        translate([10, -18, -5]) rotate([0, -30, -45])
        scale([0.8, 0.3, 2]) sphere(8, $fn=15);

        // Back flippers
        translate([-20, 12, -3]) rotate([0, 0, 30])
        scale([0.6, 0.25, 1.8]) sphere(7, $fn=15);
        translate([-20, -12, -3]) rotate([0, 0, -30])
        scale([0.6, 0.25, 1.8]) sphere(7, $fn=15);
    }

    color([0.1, 0.1, 0.1]) { // Eyes
        translate([30, 4, 14]) sphere(2, $fn=10);
        translate([30, -4, 14]) sphere(2, $fn=10);

        // Eye ridges
        translate([28, 5, 16]) rotate([0, -20, 0])
        cube([3, 2, 1], center=true);
        translate([28, -5, 16]) rotate([0, 20, 0])
        cube([3, 2, 1], center=true);
    }

    color([0.05, 0.15, 0.25]) { // Mouth
        translate([33, 0, 10]) rotate([0, -10, 0])
        difference() {
            scale([1, 0.4, 0.3]) sphere(5, $fn=15);
            translate([0, 0, 2]) cube([8, 4, 4], center=true);
        }
    }

    color([0.8, 0.7, 0.6]) { // Undershell
        difference() {
            translate([0, 0, -10]) scale([1.1, 0.9, 0.5]) sphere(18, $fn=25);
            translate([0, 0, -5]) cube([50, 40, 20], center=true);
        }
    }

    color([0.1, 0.2, 0.3]) { // Tail
        translate([-28, 0, -2]) rotate([0, 10, 0])
        scale([1, 0.6, 0.4]) sphere(8, $fn=15);
    }

    color([0.2, 0.3, 0.4]) { // Shell pattern details
        for(i = [-10:10:10]) {
            for(j = [-5:5:5]) {
                translate([i, j, 18]) 
                cylinder(h=1, r1=3, r2=1, $fn=6);
            }
        }
    }
}

