// Solosis Pokemon - Realistic Style (Medium Complexity)
// 3D printable model optimized for FDM printing

$fn = 32;

// Main colors
body_color = "lightgreen";
nucleus_color = "yellow";
eye_color = "black";
highlight_color = "white";

// Overall scale
overall_scale = 0.8;

module solosis() {
    scale([overall_scale, overall_scale, overall_scale]) {

        // Main spherical body
        color(body_color) {
            difference() {
                sphere(r = 25);

                // Hollow center for printing
                translate([0, 0, -1])
                    sphere(r = 22);
            }
        }

        // Cytoplasm layer (inner sphere)
        color(body_color) {
            translate([0, 0, -2])
                sphere(r = 23);
        }

        // Nucleus (central yellow sphere)
        color(nucleus_color) {
            translate([0, 0, -3])
                sphere(r = 12);
        }

        // Eye module
        module eye(x, y, z, rot) {
            translate([x, y, z]) {
                rotate([0, 0, rot]) {
                    // Eye white
                    color("white")
                        sphere(r = 4);

                    // Pupil
                    color(eye_color)
                        translate([0, 0, 2.5])
                            sphere(r = 2.5);

                    // Eye highlight
                    color(highlight_color)
                        translate([1, 1, 3.5])
                            sphere(r = 1);
                }
            }
        }

        // Left eye
        eye(-8, 15, 0, 0);

        // Right eye
        eye(8, 15, 0, 0);

        // Mouth (curved line)
        color("black") {
            translate([0, 18, -5])
                rotate([90, 0, 0])
                    linear_extrude(height = 0.5)
                        arc(5, [0, 180], 0.5);
        }

        // Cell membrane details (bumpy surface)
        module membrane_bump(x, y, z) {
            translate([x, y, z])
                sphere(r = 2);
        }

        // Add bumps around the surface
        for(i = [0:30:360]) {
            angle = i;
            x = 25 * cos(angle);
            y = 25 * sin(angle);
            z = 10 * sin(angle * 2);

            color(body_color) {
                translate([x * 0.8, y * 0.8, z * 0.5])
                    sphere(r = 1.5);
            }
        }

        // Pseudopods (cell extensions)
        module pseudopod(x, y, z, rot1, rot2) {
            translate([x, y, z]) {
                rotate([rot1, rot2, 0]) {
                    color(body_color) {
                        // Base cylinder
                        cylinder(h = 15, r1 = 3, r2 = 1);

                        // Tip sphere
                        translate([0, 0, 15])
                            sphere(r = 1.5);
                    }
                }
            }
        }

        // Add pseudopods around the body
        pseudopod(20, 0, 0, 0, 45);
        pseudopod(-20, 0, 0, 0, 135);
        pseudopod(0, 20, 0, 45, 90);
        pseudopod(0, -20, 0, -45, 90);

        // Cytoplasmic strands
        module strand(x, y, z, rot) {
            translate([x, y, z]) {
                rotate([0, 0, rot]) {
                    color(body_color) {
                        hull() {
                            sphere(r = 1);
                            translate([0, 8, 0])
                                sphere(r = 0.5);
                        }
                    }
                }
            }
        }

        // Add strands inside
        for(i = [0:45:360]) {
            angle = i;
            x = 15 * cos(angle);
            y = 15 * sin(angle);

            strand(x, y, -5, angle);
        }

        // Base support for printing
        color(body_color) {
            translate([0, 0, -25])
                cylinder(h = 2, r = 20);
        }
    }
}

// Helper module for arc
module arc(radius, angles, width) {
    difference() {
        circle(radius + width/2);
        circle(radius - width/2);
    }
}

// Render the model
solosis();

