module ice_cream_ball(height, top_radius, bottom_radius) {
    // Create a rounded ice cream ball shape
    hull() {
        translate([0, 0, height/2])
            sphere(top_radius);
        translate([0, 0, -height/2])
            sphere(bottom_radius);
    }
}

module face_details() {
    // Eyes
    color("blue") {
        translate([-6, -8, 15])
            sphere(3);
        translate([6, -8, 15])
            sphere(3);
    }

    // Eye pupils
    color("black") {
        translate([-6.5, -9.5, 15.5])
            sphere(1);
        translate([6.5, -9.5, 15.5])
            sphere(1);
    }

    // Mouth
    color("black") {
        translate([0, -10, 10])
            rotate([90, 0, 0])
                cylinder(h=0.5, r1=3, r2=2);
    }
}

module straw() {
    // Main straw body
    color("lightblue") {
        translate([0, 0, 25])
            rotate([0, 0, 10])
                cylinder(h=20, r1=2, r2=2);
    }

    // Straw stripes
    color("white") {
        for(i = [0:2]) {
            translate([0, 0, 25 + i*5])
                rotate([0, 0, 10])
                    cylinder(h=1, r1=2.5, r2=2.5);
        }
    }
}

module ice_crystals() {
    // Add ice crystal details
    color("lightblue") {
        for(i = [0:5]) {
            rotate([0, 0, i*60])
                translate([12*cos(i*60), 12*sin(i*60), 5])
                    rotate([0, 0, i*30])
                        cube([2, 0.5, 8]);
        }

        for(i = [0:4]) {
            rotate([0, 0, i*72 + 30])
                translate([10*cos(i*72 + 30), 10*sin(i*72 + 30), -5])
                    rotate([0, 0, i*36])
                        cube([1.5, 0.5, 6]);
        }
    }
}

module head_ball() {
    // Top ice cream head
    color("white") {
        ice_cream_ball(20, 12, 15);

        // Scoop texture
        difference() {
            ice_cream_ball(20, 12, 15);
            translate([0, 0, -2])
                ice_cream_ball(16, 11, 14);
        }
    }

    // Face
    face_details();

    // Straw on top
    straw();
}

module bottom_ball() {
    // Bottom ice cream head
    color("white") {
        translate([0, 0, -25])
            ice_cream_ball(22, 15, 12);

        // Scoop texture
        difference() {
            translate([0, 0, -25])
                ice_cream_ball(22, 15, 12);
            translate([0, 0, -27])
                ice_cream_ball(18, 14, 11);
        }
    }

    // Second face
    translate([0, 0, -25]) {
        color("blue") {
            translate([-5, -7, 13])
                sphere(2.5);
            translate([5, -7, 13])
                sphere(2.5);
        }

        color("black") {
            translate([-5.5, -8.5, 13.5])
                sphere(0.8);
            translate([5.5, -8.5, 13.5])
                sphere(0.8);

            translate([0, -9, 9])
                rotate([90, 0, 0])
                    cylinder(h=0.5, r1=2.5, r2=2);
        }
    }
}

module connecting_ice() {
    // Ice bridge connecting the two heads
    color("white") {
        union() {
            // Main connecting column
            cylinder(h=15, r1=6, r2=4);

            // Side ice formations
            for(i = [0:3]) {
                rotate([0, 0, i*90])
                    translate([5*cos(i*90), 5*sin(i*90), -7])
                        rotate([0, 0, i*45])
                            cube([3, 1, 8]);
            }
        }
    }
}

// Main assembly
scale([scale_factor, scale_factor, scale_factor]) {
    union() {
        // Bottom head
        bottom_ball();

        // Connecting ice
        connecting_ice();

        // Top head
        head_ball();

        // Ice crystals around both heads
        ice_crystals();
        translate([0, 0, -25])
            ice_crystals();
    }
}

