// Amoonguss in realistic style - medium complexity
// 3D printable design with proper wall thickness

// Main parameters
body_radius = 20;
body_height = 35;
cap_height = 15;
cap_radius = 25;
stem_radius = 8;
stem_height = 10;
total_height = body_height + stem_height + cap_height;

// Colors
cap_color = [0.6, 0.2, 0.2];    // Red cap
body_color = [0.9, 0.9, 0.8];   // Off-white body
spot_color = [0.1, 0.1, 0.1];   // Dark spots

// Main module
module amoonguss() {
    // Cap with stem
    translate([0, 0, body_height + stem_height]) {
        // Stem
        color(body_color)
        cylinder(h=stem_height, r=stem_radius, $fn=16);

        // Cap
        color(cap_color)
        difference() {
            cylinder(h=cap_height, r=cap_radius, $fn=32);
            // Cap bottom indent
            translate([0, 0, -1])
            cylinder(h=cap_height/2, r=cap_radius-5, $fn=32);
        }
    }

    // Body
    color(body_color)
    difference() {
        // Main body sphere
        sphere(r=body_radius, $fn=32);

        // Flat bottom
        translate([0, 0, -body_radius-1])
        cube([body_radius*2, body_radius*2, body_radius*2], center=true);
    }

    // Pokeball spots on cap
    translate([0, 0, body_height + stem_height + cap_height/2]) {
        for(i=[0:2]) {
            rotate([0, 0, i*120])
            translate([cap_radius*0.7, 0, 0])
            color(spot_color)
            sphere(r=3, $fn=16);
        }
    }

    // Eyes
    translate([0, body_radius-5, body_height/2]) {
        // Left eye
        translate([-8, 0, 0])
        color(spot_color)
        sphere(r=4, $fn=16);

        // Right eye
        translate([8, 0, 0])
        color(spot_color)
        sphere(r=4, $fn=16);
    }

    // Mouth
    translate([0, body_radius-2, body_height/2-5])
    rotate([90, 0, 0])
    color(spot_color)
    cylinder(h=0.5, r1=6, r2=3, $fn=16);

    // Arms (mushroom gills)
    module arm(angle) {
        rotate([0, 0, angle])
        translate([body_radius-2, 0, body_height/2-5])
        rotate([0, 90, 0])
        color(body_color)
        difference() {
            cylinder(h=15, r=4, $fn=16);
            translate([0, 0, -1])
            cylinder(h=17, r=2, $fn=16);
        }
    }

    arm(45);
    arm(-45);
    arm(135);
    arm(-135);
}

