// Alomomola - Realistic Style, Medium Complexity
// Size: ~80mm wide

$fn = 48;

module alomomola() {
    color([0.9, 0.4, 0.5]) {
        // Main body - elliptical fish shape
        union() {
            // Main body shell
            scale([1.2, 1, 0.6]) sphere(r = 30);

            // Top dorsal ridge
            for(i = [-20:5:20]) {
                translate([i, 0, 18]) 
                    scale([0.8, 0.3, 0.3]) sphere(r = 5);
            }

            // Front fins - wing-like
            translate([-25, 0, 0]) rotate([0, 90, 0]) 
                scale([1, 0.3, 1.5]) cylinder(h = 35, r1 = 15, r2 = 5);
            translate([25, 0, 0]) rotate([0, -90, 0]) 
                scale([1, 0.3, 1.5]) cylinder(h = 35, r1 = 15, r2 = 5);

            // Rear tail extension
            translate([0, -35, -5]) 
                scale([0.8, 1.2, 0.4]) sphere(r = 20);

            // Eye sockets
            difference() {
                // Left eye
                translate([-10, 20, 12]) {
                    color([1, 1, 1]) sphere(r = 6);
                    color([0.2, 0.2, 0.4]) 
                        translate([0, 0, 4]) sphere(r = 4);
                    color([0, 0, 0]) 
                        translate([0, 0, 5]) sphere(r = 2);
                }

                // Right eye
                translate([10, 20, 12]) {
                    color([1, 1, 1]) sphere(r = 6);
                    color([0.2, 0.2, 0.4]) 
                        translate([0, 0, 4]) sphere(r = 4);
                    color([0, 0, 0]) 
                        translate([0, 0, 5]) sphere(r = 2);
                }
            }

            // Pink markings pattern
            color([0.95, 0.5, 0.6]) {
                // Top fin markings
                for(x = [-15:10:15]) {
                    translate([x, 0, 20]) 
                        scale([1, 0.5, 0.5]) sphere(r = 3);
                }

                // Side markings
                for(y = [-15:10:15]) {
                    translate([-15, y, 0]) 
                        scale([0.5, 1, 0.5]) sphere(r = 2);
                    translate([15, y, 0]) 
                        scale([0.5, 1, 0.5]) sphere(r = 2);
                }
            }

            // Mouth - small curved opening
            difference() {
                translate([0, 25, 2]) 
                    scale([1, 0.3, 0.5]) sphere(r = 8);
                translate([0, 27, 2]) 
                    cube([16, 6, 6], center = true);
            }

            // Ventral fins
            translate([-15, -25, -15]) 
                rotate([0, 30, 0]) scale([1, 0.3, 0.5]) cylinder(h = 12, r1 = 6, r2 = 3);
            translate([15, -25, -15]) 
                rotate([0, -30, 0]) scale([1, 0.3, 0.5]) cylinder(h = 12, r1 = 6, r2 = 3);

            // Heart-shaped patterns on fins
            color([0.8, 0.3, 0.4]) {
                // Left fin heart
                translate([-35, 0, 0]) rotate([0, 90, 0]) {
                    scale([0.5, 0.5, 0.5]) {
                        sphere(r = 5);
                        translate([5, 0, 0]) sphere(r = 5);
                        translate([2.5, 0, 5]) sphere(r = 5);
                    }
                }

                // Right fin heart
                translate([35, 0, 0]) rotate([0, -90, 0]) {
                    scale([0.5, 0.5, 0.5]) {
                        sphere(r = 5);
                        translate([-5, 0, 0]) sphere(r = 5);
                        translate([-2.5, 0, 5]) sphere(r = 5);
                    }
                }
            }
        }
    }
}

