// Realistic Joltik Pokemon - OpenSCAD
// Medium complexity 3D printable model

// Main parameters
scale_factor = 1;
body_size = 50 * scale_factor;
leg_length = 15 * scale_factor;
eye_size = 4 * scale_factor;
ear_length = 8 * scale_factor;

// Colors
body_color = "#4A4A4A";
eye_color = "#00AAFF";
mouth_color = "#222222";
accent_color = "#FFFF00";

// Body sphere with slight oval shape
module body() {
    color(body_color)
    union() {
        // Main body
        scale([1, 1.2, 0.9])
        sphere(d=body_size, $fn=50);

        // Mouth indentation
        translate([0, -body_size/2.5, -body_size/4])
        rotate([0, 0, 0])
        color(mouth_color)
        sphere(d=body_size/3, $fn=30);
    }
}

// Eye module
module eye(x_pos, y_pos) {
    translate([x_pos, y_pos, body_size/3])
    color(eye_color)
    union() {
        // Main eye sphere
        sphere(d=eye_size, $fn=20);

        // Pupil
        translate([0, 0, eye_size/2])
        color("#000000")
        sphere(d=eye_size/2, $fn=15);

        // Highlight
        translate([eye_size/4, eye_size/4, eye_size/1.8])
        color("#FFFFFF")
        sphere(d=eye_size/4, $fn=10);
    }
}

// Ear module
module ear(x_pos) {
    translate([x_pos, body_size/2, body_size/3])
    rotate([0, 30, 0])
    union() {
        // Main ear
        color(body_color)
        scale([0.3, 1, 0.3])
        cylinder(h=ear_length, d=body_size/8, $fn=20);

        // Yellow tip
        translate([0, ear_length/2, 0])
        color(accent_color)
        scale([0.25, 0.5, 0.25])
        cylinder(h=ear_length/3, d=body_size/8, $fn=20);
    }
}

// Leg module
module leg(x_pos, y_pos, z_pos, angle) {
    translate([x_pos, y_pos, z_pos])
    rotate([90, angle, 0])
    color(body_color)
    union() {
        // Upper leg segment
        cylinder(h=leg_length/2, d=body_size/15, $fn=12);

        // Joint
        translate([0, 0, leg_length/2])
        sphere(d=body_size/12, $fn=12);

        // Lower leg segment
        translate([0, 0, leg_length/2])
        cylinder(h=leg_length/2, d=body_size/20, $fn=12);

        // Foot
        translate([0, 0, leg_length])
        scale([1, 0.5, 0.3])
        sphere(d=body_size/10, $fn=12);
    }
}

// Yellow accent spots
module accent_spot(x, y, z, size) {
    translate([x, y, z])
    color(accent_color)
    sphere(d=size, $fn=20);
}

// Assembly
union() {
    body();

    // Eyes
    eye(-body_size/4, -body_size/3);
    eye(body_size/4, -body_size/3);

    // Ears
    ear(-body_size/3);
    ear(body_size/3);

    // Legs - 4 main legs
    leg(-body_size/3, body_size/4, -body_size/3, 30);
    leg(body_size/3, body_size/4, -body_size/3, -30);
    leg(-body_size/3, -body_size/4, -body_size/3, 30);
    leg(body_size/3, -body_size/4, -body_size/3, -30);

    // Additional smaller legs (Joltik has 8 legs)
    leg(-body_size/2.5, 0, -body_size/3.5, 45);
    leg(body_size/2.5, 0, -body_size/3.5, -45);
    leg(0, body_size/3, -body_size/3.5, 15);
    leg(0, -body_size/3, -body_size/3.5, -15);

    // Yellow accent spots on body
    accent_spot(body_size/3, body_size/4, 0, body_size/10);
    accent_spot(-body_size/3, body_size/4, 0, body_size/10);
    accent_spot(0, body_size/5, body_size/4, body_size/12);
    accent_spot(body_size/4, 0, -body_size/4, body_size/15);
    accent_spot(-body_size/4, 0, -body_size/4, body_size/15);
    accent_spot(0, -body_size/5, -body_size/5, body_size/15);
}

// Galvantula - Electric/Bug type Pokemon
// Realistic style, medium complexity

module leg(segment, angle) {
    rotate([0, 0, angle]) {
        translate([segment == 1 ? 0 : 8, 0, segment == 1 ? 0 : -4]) {
            color([0.2, 0.2, 0.2]) {
                if (segment == 1) {
                    rotate([0, 90, 0]) cylinder(h=8, r=2, $fn=8);
                } else {
                    rotate([30, 0, 0]) cylinder(h=10, r=1.5, $fn=8);
                }
            }
        }
    }
}

module eye() {
    difference() {
        color([0.9, 0.9, 0.1]) sphere(r=3, $fn=16);
        translate([0, 0, 2.5]) color([0, 0, 0]) cylinder(h=3, r1=1.5, r2=0, $fn=8);
    }
}

module body() {
    // Main body
    color([0.8, 0.8, 0.8]) {
        sphere(r=12, $fn=32);
    }

    // Blue abdomen pattern
    color([0.2, 0.3, 0.8]) {
        translate([0, -15, 0]) {
            scale([1.2, 1.5, 1]) sphere(r=10, $fn=32);
        }
    }

    // Yellow stripes
    color([0.9, 0.9, 0.1]) {
        for (i = [-1, 0, 1]) {
            translate([i * 6, -10, 0]) rotate([0, 90, 0]) cylinder(h=2, r=10, $fn=3);
        }
    }
}

module mandibles() {
    color([0.3, 0.3, 0.3]) {
        translate([5, 8, -5]) rotate([45, 0, 30]) cylinder(h=8, r1=2, r2=1, $fn=6);
        translate([-5, 8, -5]) rotate([45, 0, -30]) cylinder(h=8, r1=2, r2=1, $fn=6);
    }
}

// Main assembly
union() {
    // Body
    body();

    // Head
    translate([0, 15, 5]) {
        color([0.8, 0.8, 0.8]) sphere(r=8, $fn=24);

        // Eyes
        translate([4, 2, 3]) eye();
        translate([-4, 2, 3]) eye();

        // Mandibles
        mandibles();

        // Fangs
        color([0.9, 0.9, 0.1]) {
            translate([3, 8, -3]) cylinder(h=3, r=1, $fn=8);
            translate([-3, 8, -3]) cylinder(h=3, r=1, $fn=8);
        }
    }

    // Legs (8 total)
    for (i = [0:7]) {
        angle = i * 45;
        translate([0, 0, -5]) {
            leg(1, angle);
            leg(2, angle + (i % 2 == 0 ? 10 : -10));
        }
    }

    // Electric tufts
    color([0.9, 0.9, 0.1]) {
        for (i = [0:3]) {
            angle = i * 90;
            rotate([0, 0, angle]) translate([15, 0, 0]) 
                rotate([0, 90, 0]) cylinder(h=8, r=2, $fn=8);
        }
    }
}

// Ferroseed - Realistic Style (Medium Complexity)
// Overall height ~60mm, 3D-printable with 2mm walls and angled spikes

$fn=32;  // Smooth circles

// Main colors
color_metallic = [0.5,0.5,0.5];
color_spike = [0.3,0.3,0.3];
color_eye = [0.1,0.1,0.1];
color_band = [0.6,0.6,0.6];

// Main seed body
module seed_body() {
    color(color_metallic)
    difference() {
        sphere(d=32, center=true);
        // Flatten bottom slightly for printability
        translate([0,0,-16.5])
            cube([34,34,2], center=true);
    }
}

// Single spike module angled for printability
module spike(len=12, dia=3.5, angle=35) {
    color(color_spike)
    rotate([0,angle,0])
    cylinder(h=len, d1=dia, d2=dia*0.6, center=false);
}

// Ring of spikes around body
module spike_ring(height, count, radius, len, dia) {
    for(i=[0:count-1]) {
        angle = i*360/count;
        translate([radius*sin(angle), radius*cos(angle), height])
            rotate([0,0,angle])
                spike(len, dia);
    }
}

// Eye
module eye() {
    color(color_eye)
    translate([0,15,5])
        sphere(d=6);
}

// Metallic band around middle
module band() {
    color(color_band)
    difference() {
        cylinder(h=6, d=34, center=true);
        cylinder(h=7, d=30, center=true);
    }
}

// Assembly
module ferroseed() {
    seed_body();

    // Upper spike rings
    spike_ring(height=8, count=6, radius=17, len=10, dia=3.5);
    spike_ring(height=-4, count=6, radius=17, len=10, dia=3.5);

    // Lower spike rings
    spike_ring(height=-12, count=5, radius=16.5, len=9, dia=3.2);
    spike_ring(height=-18, count=4, radius=15.5, len=8, dia=3.0);

    // Top central spike
    translate([0,0,18])
        spike(len=14, dia=4, angle=0);

    // Eye
    eye();

    // Metallic band
    band();
}

ferroseed();

