// Realistic Klink OpenSCAD Model
// Medium complexity with 3D printability in mind

// Parameters
$fa = 2;
$fs = 0.5;
gear_thickness = 6;
gear_radius = 20;
tooth_height = 4;
tooth_width = 3;
tooth_count = 12;
face_radius = 12;
eye_size = 3;
mouth_width = 8;
mouth_height = 2;
center_distance = 45;
wall_thickness = 2;

// Color definitions
color_gear = [0.85, 0.85, 0.85];
color_face = [0.95, 0.8, 0.7];
color_details = [0.3, 0.3, 0.3];

// Gear tooth module
module gear_tooth(height) {
    difference() {
        cube([tooth_width, tooth_height, height], center=true);
        translate([0, tooth_height/2, height/2])
            sphere(r=tooth_width/2);
    }
}

// Gear module with teeth
module gear(radius, thickness, tooth_count) {
    color(color_gear)
    union() {
        // Main gear body
        cylinder(h=thickness, r=radius, center=true);

        // Teeth around circumference
        for(i=[0:tooth_count-1]) {
            rotate([0, 0, i*360/tooth_count])
                translate([radius + tooth_height/2, 0, 0])
                    gear_tooth(thickness);
        }

        // Center hole
        difference() {
            cylinder(h=thickness+1, r=radius-wall_thickness, center=true);
            cylinder(h=thickness+2, r=face_radius, center=true);
        }
    }
}

// Face module for each gear
module klink_face() {
    color(color_face)
    union() {
        // Face disk
        translate([0, 0, gear_thickness/2 + 0.1])
            cylinder(h=1, r=face_radius-1, center=false);

        // Eyes
        color(color_details) {
            translate([face_radius/3, face_radius/3, gear_thickness/2 + 1.5])
                sphere(r=eye_size);
            translate([-face_radius/3, face_radius/3, gear_thickness/2 + 1.5])
                sphere(r=eye_size);

            // Mouth
            translate([0, -face_radius/3, gear_thickness/2 + 1.5])
                rotate([90, 0, 0])
                    cylinder(h=mouth_width, r=mouth_height, center=true);

            // X mark on forehead
            translate([0, face_radius/2, gear_thickness/2 + 1.5])
                union() {
                    rotate([0, 0, 45])
                        cube([mouth_width/2, 1, 1], center=true);
                    rotate([0, 0, -45])
                        cube([mouth_width/2, 1, 1], center=true);
                }
        }
    }
}

// Central connector axle
module connector() {
    color(color_gear)
    difference() {
        // Main axle
        cylinder(h=center_distance, r=gear_thickness, center=true);

        // Hollow center
        cylinder(h=center_distance+1, r=gear_thickness-wall_thickness, center=true);
    }
}

// Complete Klink assembly
module klink() {
    // First gear
    translate([0, 0, -center_distance/2])
        union() {
            gear(gear_radius, gear_thickness, tooth_count);
            rotate([0, 0, 15]) klink_face();
        }

    // Second gear
    translate([0, 0, center_distance/2])
        union() {
            gear(gear_radius, gear_thickness, tooth_count);
            rotate([0, 0, -15]) klink_face();
        }

    // Connecting axle
    connector();
}

// Render the model
klink();

// Klang Pokemon - Medium complexity realistic style
// Sized for 3D printing (~80mm diameter)

// Colors
