color_center = [0.5, 0.5, 0.6];
color_face = [0.9, 0.9, 0.95];
color_detail = [0.3, 0.3, 0.4];

// Main module
module klang() {
    // Outer gear (larger gear)
    rotate([0, 0, 0]) 
        color(color_gear)
        gear(40, 8, 15, 5);

    // Inner gear (smaller gear)
    translate([0, 0, 20])
        rotate([0, 0, 15])
        color(color_gear)
        gear(30, 6, 12, 4);

    // Center sphere
    translate([0, 0, 10])
        color(color_center)
        sphere(15, $fn=32);

    // Face features
    translate([0, 0, 25])
        rotate([0, 0, 0])
        color(color_face)
        face_details();

    // Connection pins
    for(i=[0:7]) {
        rotate([0, 0, i*45])
        translate([35, 0, 10])
        color(color_detail)
        cylinder(h=5, r=3, $fn=16);
    }
}

// Gear module with teeth
module gear(radius, teeth, thickness, tooth_height) {
    union() {
        // Main gear body
        cylinder(h=thickness, r=radius, $fn=64);

        // Teeth
        for(i=[0:teeth-1]) {
            rotate([0, 0, i*(360/teeth)])
            translate([radius, 0, -1])
            cube([tooth_height, 8, thickness+2], center=true);
        }

        // Center hole
        difference() {
            cylinder(h=thickness, r=radius, $fn=64);
            translate([0, 0, -1])
            cylinder(h=thickness+2, r=radius*0.3, $fn=32);
        }
    }
}

// Face details module
module face_details() {
    // Main face disc
    cylinder(h=3, r=12, $fn=32);

    // Left eye
    translate([-5, 5, 1.5])
    color(color_detail)
    cylinder(h=2, r=2, $fn=16);

    // Right eye
    translate([5, 5, 1.5])
    color(color_detail)
    cylinder(h=2, r=2, $fn=16);

    // Mouth
    translate([0, -2, 1.5])
    color(color_detail)
    rotate([90, 0, 0])
    cylinder(h=6, r=1, $fn=16);

    // Eye highlights
    translate([-4.5, 5.5, 3])
    color(color_face)
    sphere(0.5, $fn=8);

