// Klinklang Pokemon in realistic style - medium complexity
// Designed for 3D printing with proper wall thickness

$fn = 32;
wall_thickness = 2;

module klink_gear(outer_radius, inner_radius, thickness, teeth_count) {
    // Main gear body
    difference() {
        cylinder(h = thickness, r = outer_radius, center = true);
        cylinder(h = thickness + 1, r = inner_radius, center = true);
    }

    // Gear teeth
    for (i = [0:teeth_count-1]) {
        rotate([0, 0, i * 360/teeth_count]) 
            translate([outer_radius + 2, 0, 0])
                cube([4, 2, thickness], center = true);
    }
}

module mini_gear(outer_radius, inner_radius, thickness, teeth_count) {
    color("DarkSlateGray")
    klink_gear(outer_radius, inner_radius, thickness, teeth_count);
}

module main_body() {
    // Central gear body
    color("Goldenrod")
    klink_gear(25, 8, 8, 16);

    // Outer ring gear
    difference() {
        color("Goldenrod")
        cylinder(h = 8, r = 35, center = true);
        cylinder(h = 9, r = 28, center = true);
    }

    // Gear teeth on outer ring
    for (i = [0:11]) {
        rotate([0, 0, i * 30]) 
            translate([31.5, 0, 0])
                color("Goldenrod")
                cube([3, 6, 8], center = true);
    }

    // Small gear on top
    translate([0, 0, 8])
        rotate([0, 0, 15])
            mini_gear(12, 4, 3, 12);
}

module klang_body() {
    // Klang body
    color("DarkSlateGray")
    klink_gear(22, 6, 7, 14);

    // Red core
    difference() {
        color("FireBrick")
        sphere(r = 6);
        sphere(r = 3);
    }
}

module klink_body() {
    // Klink body
    color("DarkSlateGray")
    klink_gear(18, 5, 5, 12);

    // Red core
    difference() {
        color("FireBrick")
        sphere(r = 5);
        sphere(r = 2.5);
    }
}

module connection_plate() {
    // Connection plate between gears
    color("DimGray")
    difference() {
        cube([40, 8, 2], center = true);
        cube([36, 4, 3], center = true);
    }
}

module power_indicator() {
    // Power indicator on top
    translate([0, 0, 15])
        color("Red")
        difference() {
            cylinder(h = 8, r1 = 4, r2 = 2, center = false);
            cylinder(h = 9, r1 = 2, r2 = 0, center = false);
        }
}

module assembly() {
    // Main Klinklang assembly
    main_body();

    // Klang body connected at angle
    translate([0, 0, 6])
        rotate([0, 90, 45])
            klang_body();

    // Klink body connected at opposite angle
    translate([0, 0, -6])
        rotate([0, 90, -45])
            klink_body();

    // Connection plates
    translate([30, 0, 0])
        rotate([0, 90, 0])
            connection_plate();

    translate([-30, 0, 0])
        rotate([0, 90, 0])
            connection_plate();

    // Power indicator
    power_indicator();
}

// Scale to reasonable printing size (80mm max dimension)
scale(0.8)
    assembly();

