module triangle_rounded(h = 30, r = 3, w = 100) {
    // equilateral triangle with rounded corners
    p = [
        [0, w * sqrt(3)/3],           // top
        [-w/2, -w * sqrt(3)/6],       // bottom left
        [ w/2, -w * sqrt(3)/6]        // bottom right
    ];

    // offset inward then outward to round corners
    linear_extrude(height = h)
        offset(r = r)
            offset(r = -r)
                polygon(points = p);
}

