module triangle_equilateral(h = 10, side = 60, r = 3) {
    // 2D rounded equilateral triangle
    a = side;
    h_tri = sqrt(3)/2 * a;
    x1 = -a/2;
    y1 = -h_tri/3;
    x2 = a/2;
    y2 = -h_tri/3;
    x3 = 0;
    y3 = 2*h_tri/3;

    // Offset points for rounded corners
    offset = r * tan(30);

    triangle_pts = [
        [x1 + offset, y1],
        [x2 - offset, y1],
        [x2, y1 + offset],
        [x2 - offset/sqrt(3), y3 - offset],
        [x3, y3 - offset],
        [x1 + offset/sqrt(3), y3 - offset],
        [x1, y1 + offset]
    ];

    linear_extrude(height = h)
        polygon(points = triangle_pts);
}

