module triangle_rounded(height = 20, side = 100, r = 8) {
    h = side * sqrt(3)/2;                 // triangle height
    corner = [[0, 0], [r, 0], [0, r]];    // quarter-circle template

    points = [
        [r, r],                           // bottom-left arc start
        [side/2, r],                      // bottom edge
        [side - r, r],                    // bottom-right arc start
        for(i = [0:10:90]) [              // bottom-right corner arc
            side - r + r*cos(i),
            r + r*sin(i)
        ],
        [side - r, h - r],                // right edge
        for(i = [90:10:180]) [            // top corner arc
            side/2 + r*cos(i),
            h - r + r*sin(i)
        ],
        [r, h - r],                       // left edge
        for(i = [180:10:270]) [           // bottom-left corner arc
            r + r*cos(i),
            r + r*sin(i)
        ]
    ];

    linear_extrude(height = height, center = true)
        polygon(points = points);
}

