module square_small(radius=2, size=80) {
    // rounded square base profile
    points = [
        [0, radius],
        [radius, 0],
        [size-radius, 0],
        [size, radius],
        [size, size-radius],
        [size-radius, size],
        [radius, size],
        [0, size-radius]
    ];

    // simple rounded corner arcs
    arc = [
        for(i=[0:3]) each
        let(a=i*90, n=5)
        [size-radius + radius*cos(a+90/n), radius + radius*sin(a+90/n)],
        for(i=[1:n-1]) [size-radius + radius*cos(a+90*i/n), radius + radius*sin(a+90*i/n)]
    ];

    polygon(concat(points, arc));
}

