module square_unit(size = 80, radius = 8, height = 12) {
    // Define 2D rounded square profile
    corner = [
        for (a = [0:5:90]) [radius - radius*cos(a), radius - radius*sin(a)]
    ];

    profile = [
        [radius, 0],
        [size - radius, 0],
        each [for (p = corner) [size - p[1], p[0]]],
        [size, size - radius],
        each [for (p = corner) [size - p[0], size - p[1]]],
        [radius, size],
        each [for (p = corner) [p[1], size - p[0]]],
        [0, radius],
        each [for (p = corner) [p[0], p[1]]]
    ];

    linear_extrude(height = height, center = true)
        polygon(profile);
}

