module square_with_hole(size = 80, thickness = 12, fillet = 6, hole_d = 30) {
    // Outer square with rounded corners
    r = fillet;
    s = size/2 - r;
    outer = [
        [-s, -s-r], [-s-r, -s], [-s-r,  s],
        [-s,  s+r], [ s,  s+r], [ s+r,  s],
        [ s+r, -s], [ s, -s-r]
    ];

    difference() {
        linear_extrude(height = thickness)
            offset(r = r)
                polygon(points = outer);

        // Centered cylindrical hole
