    w = size / 2 - radius;
    pts = [
        [-w, -w],
        [-w,  w],
        [ w,  w],
        [ w, -w]
    ];
    offset_pts = [
        for (p = pts) let(a = atan2(p.y, p.x))
            [p.x + radius * cos(a), p.y + radius * sin(a)]
