        [w, r],
        [w, h-r],
        [w-r, h],
        [r, h],
        [0, h-r],
        [0, r]
    ];

    // Arc segments for rounded corners
    corner = 8;
    for (a = [0:90/corner:90-90/corner]) {
        points = concat(points, [[w-r + r*cos(a), r + r*sin(a)]]);
    }
    for (a = [90:90/corner:180-90/corner]) {
