    }
    for (a = [180:90/corner:270-90/corner]) {
        points = concat(points, [[r + r*cos(a), h-r + r*sin(a)]]);
    }
    for (a = [270:90/corner:360-90/corner]) {
        points = concat(points, [[r + r*cos(a), r + r*sin(a)]]);
    }

    // Close polygon
