    points = [
        [r, 0], [w - r, 0], [w, r], [w, h - r],
        [w - r, h], [r, h], [0, h - r], [0, r]
    ];

    // outer path
    outer = concat(
        [for (i = [0:7]) points[i]],
        [[r, 0]]
    );

    // inner hole path
    hole = [
        [w/2 - hr, h/2],
        [w/2, h/2 - hr],
        [w/2 + hr, h/2],
        [w/2, h/2 + hr]
    ];

    linear_extrude(height = t)
    difference() {
        polygon(points = outer, paths = [range(0, len(outer) - 1)]);
        polygon(points = hole, paths = [[0, 1, 2, 3]]);
    }
}

