module pentagon(r = 50, h = 10, fillet = 3) {
    // Outer pentagon points
    a = 360/5;
    pts = [for(i=[0:4]) [r*cos(i*a-90), r*sin(i*a-90)]];

    // Filleted inner pentagon (offset inwards)
    r2 = r - fillet;
    pts2 = [for(i=[0:4]) [r2*cos(i*a-90), r2*sin(i*a-90)]];

    // Build shape
    difference() {
        linear_extrude(height = h)
            polygon(pts);

        // Round top edges
        translate([0, 0, h - fillet])
            linear_extrude(height = fillet + 0.01)
                polygon(pts2);

        // Round bottom edges
        translate([0, 0, -0.01])
            linear_extrude(height = fillet + 0.01)
                polygon(pts2);
    }
}

