module pentagon_rounded(r = 50, h = 20, fillet = 5) {
    sides = 5;
    angle_step = 360 / sides;

    // outer pentagon
    outer_pts = [for (i = [0:sides-1])
        r * [cos(i*angle_step), sin(i*angle_step)]
    ];

    // inner pentagon offset for rounding
    inner_pts = [for (i = [0:sides-1])
        (r - fillet) * [cos(i*angle_step), sin(i*angle_step)]
    ];

    // prepare rounded polygon
    offset(r = fillet) offset(r = -fillet)
        polygon(outer_pts);

    // extrude
    linear_extrude(height = h, center = true)
        offset(r = fillet) offset(r = -fillet)
            polygon(outer_pts);
}

