    angle_step = 360 / sides;

    // sharp hexagon points
    pts = [for(i=[0:sides-1]) 
           [r*cos(i*angle_step), r*sin(i*angle_step)]];

    // offset inwards for rounded edges
    offset_pts = [for(p=pts) 
