    sides = 10;
    angle_step = 360 / sides;

    // outer vertices
    outer_pts = [for (i = [0:sides-1]) 
        [r * cos(i*angle_step), r * sin(i*angle_step)]];

    // inner vertices for rounded edges
    inner_pts = [for (i = [0:sides-1]) 
        [(r - fillet) * cos(i*angle_step + angle_step/2), 
         (r - fillet) * sin(i*angle_step + angle_step/2)]];

    // build polygon with rounded edges
    polygon_pts = [];
    for (i = [0:sides-1]) {
        next_i = (i + 1) % sides;
        polygon_pts = concat(polygon_pts, [
            outer_pts[i],
            inner_pts[i],
            outer_pts[next_i]
        ]);
    }

    linear_extrude(height = h, center = center)
        polygon(polygon_pts);
}

