// Realistic 3D-printable hammer
// Overall length ~90 mm, printable on FDM printer

$fn = 32; // Smooth curves

// Colors
handle_color = "SaddleBrown";
metal_color = "Silver";
grip_color = "DarkRed";
marking_color = "Black";

// Main module
module hammer() {
    union() {
        // Handle assembly
        translate([0, 0, 0]) handle();
        // Head assembly
        translate([0, 0, 35]) head();
    }
}

// Handle with grip
module handle() {
    color(handle_color) {
        // Main handle shaft
        cylinder(h = 35, d1 = 10, d2 = 8, center = false);

        // Wider base for balance
        translate([0, 0, -2]) cylinder(h = 4, d = 12);
    }

    // Rubber grip rings
    color(grip_color) {
        for (i = [5:5:30]) {
            translate([0, 0, i]) 
                difference() {
                    cylinder(h = 3, d = 11);
                    cylinder(h = 3.1, d = 9);
                }
        }
    }
}

// Hammer head
module head() {
    color(metal_color) {
        union() {
            // Main head body
            translate([0, 0, 0]) {
                // Rectangular center
                cube([20, 12, 25], center = true);
            }

            // Claw (back end)
            translate([0, 0, 18]) {
                difference() {
                    // Main claw shape
                    union() {
                        // Vertical section
                        cube([12, 12, 15], center = true);
                        // Curved claw
                        rotate([90, 0, 0])
                            cylinder(h = 8, d = 12);
                    }
                    // Remove center for claw gap
                    translate([0, -6, 0])
                        cube([8, 12, 20], center = true);
                }
            }

            // Strike face (front end)
            translate([0, 0, -12.5]) {
                // Main strike surface
                cylinder(h = 2, d = 18);

                // Beveled edge
                difference() {
                    cylinder(h = 2, d = 22);
                    cylinder(h = 2.1, d = 18);
                }
            }

            // Handle connection neck
            translate([0, 0, -25]) {
                cylinder(h = 8, d1 = 12, d2 = 10);
            }
        }
    }

    // Manufacturer marking on side
    color(marking_color) {
        translate([10.1, 0, 8])
            rotate([0, 90, 0])
                linear_extrude(height = 0.5)
                    text("HAMMER", size = 3, halign = "center");
    }
}

// Render the hammer
hammer();

// Realistic medium-complexity screwdriver (≈85 mm total)
$fn = 50;

// Overall dimensions
handle_len = 45;
shaft_len  = 40;
tip_len    = 6;

// Module for the Phillips tip profile
module phillips_tip(radius, depth) {
    difference() {
        cylinder(h = depth, r = radius, center = false);
        for(a = [0:90:359])
            rotate([0, 0, a])
                translate([0, 0, -1])
                    cube([radius*2.2, 1.2, depth + 2], center = true);
    }
}

// Handle grip ridges
module grip_ridges() {
    color("DarkOrange")
    for(i = [0:3:handle_len-7]) {
        translate([0, 0, i])
            difference() {
                cylinder(h = 2, r1 = 10, r2 = 9.6);
                cylinder(h = 2.1, r = 7.5);
            }
    }
}

// Handle main body
module handle() {
    color("SaddleBrown") {
        // Core
        cylinder(h = handle_len, r = 10);

        // End cap
        translate([0, 0, handle_len])
            cylinder(h = 4, r1 = 10, r2 = 8);

        // Grip pattern
        grip_ridges();
    }
}

// Shaft
module shaft() {
    color("Silver") {
        // Main rod
        cylinder(h = shaft_len, r = 3.5);

        // Transition collar
        translate([0, 0, -1])
            cylinder(h = 3, r1 = 5, r2 = 3.5);
    }
}

// Phillips tip
module tip() {
    color("Silver")
    translate([0, 0, shaft_len])
        phillips_tip(radius = 3.5, depth = tip_len);
}

// Assembly
handle();
translate([0, 0, handle_len])
    shaft();
translate([0, 0, handle_len + shaft_len])
    tip();

// Realistic Wrench Tool - OpenSCAD
// Medium complexity 3D printable wrench

$fn = 50;

// Main colors
$wrench_color = [0.7, 0.7, 0.7];
$grip_color = [0.2, 0.2, 0.2];

// Overall dimensions
wrench_length = 80;
handle_length = 55;
handle_width = 10;
handle_height = 8;
head_width = 18;
head_thickness = 8;
jaw_opening = 12;
jaw_depth = 6;

module handle() {
    color($grip_color) {
        // Main handle body
        translate([0, 0, -handle_height/2])
            cube([handle_length, handle_width, handle_height], center=false);

        // Grip pattern - recessed grooves
        for(i = [5:5:handle_length-10]) {
            translate([i, handle_width/4, -handle_height/2])
                cube([1, handle_width/2, handle_height], center=false);
        }

        // End rounding
        translate([handle_length-5, handle_width/2, 0])
            rotate([0, 90, 0])
                cylinder(h=handle_width, r=5, center=true);
    }
}

module hex_jaw() {
    color($wrench_color) {
        // Fixed jaw
        translate([handle_length, 0, 0])
            difference() {
                // Main jaw body
                cube([head_width, head_width, head_thickness], center=true);

                // Hex opening
                translate([head_width/2, 0, 0])
                    rotate([0, 0, 30])
                        cylinder(h=head_thickness+2, r=jaw_opening/2/sin(60), center=true);

                // Cut to create jaw opening
                translate([head_width/2, 0, -head_thickness/2-1])
                    cube([jaw_depth*2, jaw_opening+2, head_thickness+2], center=true);
            }

        // Movable jaw (simplified as fixed)
        translate([handle_length, 0, 0])
            cube([jaw_depth, jaw_opening, head_thickness], center=true);
    }
}

module wrench_head() {
    color($wrench_color) {
        // Transition from handle to head
        translate([handle_length-10, 0, 0])
            union() {
                // Tapered section
                hull() {
                    translate([0, -handle_width/2, -handle_height/2])
                        cube([20, handle_width, handle_height]);
                    translate([10, -head_width/2, -head_thickness/2])
                        cube([1, head_width, head_thickness]);
                }

                // Reinforcement ribs
                for(side = [-1, 1]) {
                    translate([handle_length-5, side*(handle_width/2+2), 0])
                        rotate([90, 0, 0])
                            cylinder(h=8, r1=3, r2=1);
                }
            }

        // Head structure
        hex_jaw();
    }
}

// Size markings
module markings() {
    color([0, 0, 0]) {
        // Metric size marking
        translate([handle_length/2, handle_width+1, 0])
            rotate([90, 0, 0])
                linear_extrude(height=0.5)
                    text("12mm", size=3, halign="center");
    }
}

// Assembly
union() {
    handle();
    wrench_head();
    markings();
}

// Realistic pliers model - medium complexity
// Overall size ~80mm

// Color definitions
$fn = 32;

module handle_grip() {
    // Main handle cylinder
    color("darkblue")
    difference() {
        cylinder(h=35, d=8);
        // Grip texture - hexagonal pattern
        for (i = [0:4]) {
            translate([0, 0, i*7+3.5])
                cylinder(h=2, d=7.5);
        }
    }

    // End cap
    translate([0, 0, 35])
        color("silver")
        cylinder(h=3, d=9);
}

module handle_bar() {
    // Metal shaft
    color("silver")
    difference() {
        cylinder(h=40, d=5);
        // Hollow center
        cylinder(h=40, d=3);
    }
}

module pivot_pin() {
    // Central pivot bolt
    color("silver")
    difference() {
        cylinder(h=10, d=6);
        translate([0, 0, -1])
            cylinder(h=12, d=4);
    }

    // Bolt head
    translate([0, 0, 5])
        cylinder(h=2, d=8);
}

module jaw_half() {
    // Cutters section
    color("silver")
    translate([0, 0, -15])
        difference() {
            cube([8, 3, 20], center=true);
            // Cutting edge
            translate([-4, 0, -5])
                rotate([0, 30, 0])
                cube([10, 4, 10]);
        }

    // Gripping section
    translate([0, 0, 5])
        difference() {
            cube([8, 3, 10], center=true);
            // Grip teeth
            for (i = [0:3]) {
                translate([-3.5 + i*2.3, 0, -2.5])
                    cube([0.5, 3.2, 0.3]);
            }
        }
}

module pliers_half() {
    handle_grip();

    translate([0, 0, 35])
        handle_bar();

    translate([0, 0, 75])
        rotate([0, 30, 0])
        jaw_half();
}

// Main assembly
// Left half
pliers_half();

// Right half - mirrored
mirror([1, 0, 0])
    pliers_half();

// Central pivot
translate([0, 0, 75])
    rotate([90, 0, 0])
    pivot_pin();

// Realistic Hand Saw - 3D Printable
// Medium complexity saw with blade, handle, and grip details

// Overall dimensions
blade_length = 70;
blade_width = 20;
blade_thickness = 1.2;
handle_length = 25;
handle_diameter = 18;
grip_depth = 3;

module saw_blade() {
    // Main blade body
    color("silver") 
    difference() {
        // Tapered blade shape
        hull() {
            translate([0, 0, blade_thickness/2])
                cube([blade_length, blade_width*0.4, blade_thickness], center=true);
            translate([blade_length*0.8, 0, blade_thickness/2])
                cube([blade_length*0.2, blade_width, blade_thickness], center=true);
        }

        // Blade hole
        translate([blade_length*0.9, 0, 0])
            cylinder(h=blade_thickness+0.1, r=2, center=true);

        // Teeth pattern
        for(i = [0:2:blade_length-5]) {
            translate([i-blade_length/2, blade_width/2+0.1, 0])
                rotate([90, 0, 45])
                    cylinder(h=blade_thickness+0.2, r1=0.8, r2=0.2, center=true);
            translate([i-blade_length/2+1, blade_width/2+0.1, 0])
                rotate([90, 0, -45])
                    cylinder(h=blade_thickness+0.2, r1=0.2, r2=0.8, center=true);
        }
    }

    // Blade spine reinforcement
    color("silver")
    translate([0, 0, blade_thickness+0.1])
        cube([blade_length, 2, 1], center=true);
}

module saw_handle() {
    // Main handle body
    color("burlywood") difference() {
        hull() {
            sphere(d=handle_diameter);
            translate([handle_length, 0, 0])
                sphere(d=handle_diameter*0.8);
        }

        // Grip grooves
        for(i = [0:4]) {
            rotate([0, i*360/5, 0])
                translate([handle_length*0.5, 0, 0])
                    cube([handle_length*1.2, 2, grip_depth*2], center=true);
        }

        // Ergonomic finger grooves
        for(i = [0:3]) {
            translate([handle_length*0.2 + i*handle_length/4, 0, -handle_diameter/2])
                rotate([90, 0, 0])
                    cylinder(h=handle_diameter*0.8, r=handle_diameter/0.8, center=true);
        }
    }

    // Handle grip texture rings
    color("black")
    for(i = [0:3]) {
        translate([handle_length*0.3 + i*handle_length/4, 0, 0])
            rotate_extrude()
                translate([handle_diameter/2-0.5, 0, 0])
                    circle(r=0.5);
    }

    // Handle hole for hanging
    color("burlywood")
    translate([handle_length*0.9, 0, 0])
        rotate([0, 90, 0])
            cylinder(h=handle_diameter*2, r=2, center=true);
}

// Assembly
module saw_assembly() {
    saw_blade();

    // Handle attachment
    translate([blade_length*0.5, 0, 0])
        rotate([0, 0, 90])
            saw_handle();

    // Rivets securing blade to handle
    color("gray")
    for(i = [-1:1]) {
        translate([blade_length*0.45, i*handle_diameter*0.25, blade_thickness/2+1])
            cylinder(h=2, r=1.5);
    }
}

// Render the complete saw
saw_assembly();

// Chisel Tool - Medium Complexity Realistic Style
// Woodworking chisel with wooden handle and metal blade

// Overall dimensions (mm)
blade_length = 30;
blade_width = 12;
blade_thickness = 3;
handle_length = 70;
handle_diameter = 15;
ferrule_length = 10;
ferrule_diameter = 16;

// Colors
handle_color = "BurlyWood";
blade_color = "Silver";
ferrule_color = "Goldenrod";

// Handle grip rings
grip_ring_count = 5;
grip_ring_width = 2;
grip_ring_spacing = 8;

// Main chisel module
module chisel() {
    // Handle
    color(handle_color) {
        // Main handle body
        cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // Handle end cap (slightly rounded)
        translate([0, 0, handle_length])
            sphere(d=handle_diameter, $fn=32);

        // Grip rings
        for(i = [1:grip_ring_count]) {
            translate([0, 0, handle_length - (i * grip_ring_spacing) - grip_ring_width])
                cylinder(h=grip_ring_width, d=handle_diameter + 2, $fn=32);
        }
    }

    // Ferrule (metal collar)
    color(ferrule_color) {
        translate([0, 0, handle_length - ferrule_length])
            cylinder(h=ferrule_length + 2, d=ferrule_diameter, $fn=32);
    }

    // Blade
    color(blade_color) {
        // Blade shank
        translate([0, 0, handle_length - ferrule_length - blade_length])
            cylinder(h=blade_length - 10, d1=blade_width, d2=blade_width * 0.8, $fn=4);

        // Blade body
        translate([0, 0, handle_length - ferrule_length - blade_length - 5])
            cube([blade_width, blade_thickness, 15], center=true);

        // Chisel cutting edge
        translate([0, 0, handle_length - ferrule_length - blade_length - 12])
            cube([blade_width, blade_thickness, 5], center=true);

        // Beveled cutting edge
        translate([0, 0, handle_length - ferrule_length - blade_length - 14.5])
            rotate([90, 0, 0])
                cylinder(h=blade_width/2, d1=blade_thickness, d2=0.5, $fn=3);
    }

    // Blade spine
    color(blade_color) {
        translate([0, blade_thickness/2 + 0.5, handle_length - ferrule_length - blade_length])
            cube([blade_width, 1, blade_length], center=true);
    }
}

// Create the chisel
chisel();

// OpenSCAD File Tool - Realistic Medium Complexity
// A hand file with ergonomic handle and proper teeth pattern

$fn = 50;

// File dimensions (mm)
file_length = 150;
handle_length = 60;
blade_width = 20;
blade_thickness = 4;
tang_length = 35;

// Colors
handle_color = "BurlyWood";
metal_color = "Silver";

// Main assembly
module file_tool() {
    union() {
        // Handle assembly
        translate([0, 0, 0]) handle();

        // Tang (hidden part inside handle)
        translate([handle_length - tang_length, 0, 0]) tang();

        // File blade
        translate([handle_length, 0, 0]) blade();

        // Tip detail
        translate([handle_length + file_length - 5, 0, 0]) tip();
    }
}

// Ergonomic handle with grip pattern
module handle() {
    color(handle_color) {
        difference() {
            // Main handle body
            rotate([0, 90, 0]) cylinder(h = handle_length, d1 = 28, d2 = 22, center = false);

            // Grip grooves
            for(i = [5:8:handle_length-10]) {
                translate([i, 0, 0]) rotate([0, 90, 0]) 
                    cylinder(h = 4, d = 24, center = true);
            }
        }

        // End cap detail
        translate([0, 0, 0]) rotate([0, 90, 0]) 
            cylinder(h = 5, d = 30, center = false);
    }
}

// Tang (tapered end that goes into handle)
module tang() {
    color(metal_color) {
        difference() {
            // Main tang body
            cube([tang_length, 12, 6], center = true);

            // Rounded corners
            translate([tang_length/2, 6, 3]) sphere(d = 6);
            translate([tang_length/2, -6, 3]) sphere(d = 6);
            translate([tang_length/2, 6, -3]) sphere(d = 6);
            translate([tang_length/2, -6, -3]) sphere(d = 6);
        }
    }
}

// File blade with teeth pattern
module blade() {
    color(metal_color) {
        difference() {
            // Main blade body
            cube([file_length, blade_width, blade_thickness], center = true);

            // Tapered edges
            translate([0, blade_width/2, 0]) 
                cube([file_length, 2, blade_thickness], center = true);
            translate([0, -blade_width/2, 0]) 
                cube([file_length, 2, blade_thickness], center = true);

            // Teeth pattern - single cut file
            for(i = [5:3:file_length-10]) {
                // Top teeth
                translate([i, blade_width/2 - 1, blade_thickness/2]) 
                    cube([0.5, blade_width - 2, 0.3]);

                // Bottom teeth
                translate([i, blade_width/2 - 1, -blade_thickness/2 + 0.3]) 
                    cube([0.5, blade_width - 2, 0.3]);
            }

            // Safe edges (smooth sides)
            translate([0, blade_width/2 - 1, 0]) 
                cube([file_length, 2, blade_thickness + 0.1], center = true);
            translate([0, -blade_width/2 + 1, 0]) 
                cube([file_length, 2, blade_thickness + 0.1], center = true);
        }
    }
}

// Rounded tip
module tip() {
    color(metal_color) {
        translate([-2.5, 0, 0]) 
            scale([1, 0.8, 0.8]) sphere(d = blade_width);
    }
}

// Render the file
file_tool();

// Realistic Hand-Held Electric Drill - Medium Complexity
// Overall length ~95 mm, printable without supports

$fn = 50;

// Main colors
handle_col = "DarkSlateGray";
body_col   = "FireBrick";
metal_col  = "Silver";

// Overall scale for 3D printing
scale = 0.8;

module drill_assembly() {
    scale([scale,scale,scale]) {
        // Handle with ergonomic grip
        color(handle_col) {
            translate([0,0,0])
                rotate([0,90,0])
                    cylinder(h = 30, d1 = 24, d2 = 28, center = true);

            // Rubber grip rings
            for(i = [-12:6:12]) {
                translate([i,0,0])
                    rotate([0,90,0])
                        cylinder(h = 4, d = 30, center = true);
            }
        }

        // Main motor body
        color(body_col) {
            translate([30,0,0])
                rotate([0,90,0])
                    cylinder(h = 45, d = 28);
        }

        // Chuck collar
        color(metal_col) {
            translate([52,0,0])
                rotate([0,90,0])
                    cylinder(h = 8, d = 22);

            // Chuck jaws (simplified as rings)
            for(i = [0:2.5:5]) {
                translate([56+i,0,0])
                    rotate([0,90,0])
                        cylinder(h = 2, d = 12 - i*1.2);
            }
        }

        // Trigger guard
        color(handle_col) {
            difference() {
                translate([15,-12,0])
                    cube([16,24,20], center = true);
                translate([15,-12,0])
                    cube([12,20,16], center = true);
            }
        }

        // Trigger
        color("Black") {
            translate([15,-10,5])
                cube([10,4,8], center = true);
        }

        // Forward/reverse slider
        color("Black") {
            translate([15,10,5])
                cube([8,3,4], center = true);
        }

        // Side handle mount ring
        color(metal_col) {
            translate([35,0,-15])
                rotate([90,0,0])
                    cylinder(h = 8, d = 16);
        }

        // Ventilation slots
        color(body_col) {
            for(i = [1:3:10]) {
                translate([28+i*2,0,12])
                    cube([1.5,26,4], center = true);
            }
        }

        // Power cable
        color("Black") {
            translate([-15,0,0])
                rotate([0,90,0])
                    cylinder(h = 25, d = 5);
        }
    }
}

drill_assembly();

// Realistic 3D-printable spirit level
// Overall length: 80 mm, height: 35 mm, thickness: 14 mm

$fn = 36;

module main_body() {
    // Extruded aluminum channel profile
    difference() {
        // Outer block
        translate([-40, -7, 0])
            cube([80, 14, 35], center = false);

        // Center cavity
        translate([-38, -5, 2])
            cube([76, 10, 31], center = false);

        // Top viewing window
        translate([-36, -6, 28])
            cube([72, 12, 5], center = false);
    }
}

module vial_housing() {
    // Vertical vial (plumb)
    color([0.9,0.9,0.9])
    difference() {
        translate([0, 0, 18])
            cube([12, 9, 12], center = true);
        translate([0, 0, 18])
            cylinder(h = 10, r = 4.5, center = true);
    }

    // Horizontal vial
    color([0.9,0.9,0.9])
    difference() {
        translate([-25, 0, 18])
            cube([12, 9, 12], center = true);
        translate([-25, 0, 18])
            rotate([0,90,0])
                cylinder(h = 10, r = 4.5, center = true);
    }

    // 45-degree vial
    color([0.9,0.9,0.9])
    difference() {
        translate([25, 0, 18])
            cube([12, 9, 12], center = true);
        translate([25, 0, 18])
            rotate([0,45,90])
                cylinder(h = 10, r = 4.5, center = true);
    }
}

module vial_fill() {
    // Liquid fills (yellowish green)
    color([0.5,0.7,0.2,0.6])
    union() {
        // Vertical vial fill
        translate([0, 0, 18])
            sphere(r = 4.4);

        // Horizontal vial fill
        translate([-25, 0, 18])
            rotate([0,90,0])
                sphere(r = 4.4);

        // 45-degree vial fill
        translate([25, 0, 18])
            rotate([0,45,90])
                sphere(r = 4.4);
    }

    // Bubbles (white)
    color("white")
    union() {
        // Vertical bubble
        translate([0, 0, 20])
            sphere(r = 1.2);

        // Horizontal bubble
        translate([-25, 0, 20])
            rotate([0,90,0])
                sphere(r = 1.2);

        // 45-degree bubble
        translate([25, 0, 20])
            rotate([0,45,90])
                sphere(r = 1.2);
    }
}

module measurement_marks() {
    // Raised graduation marks
    for (i = [-36:6:36]) {
        if (i != 0) {
            translate([i, -6.5, 1])
                cube([0.5, 1, 0.5], center = true);
        }
    }

    // Center zero mark
    translate([0, -6.5, 1.5])
        cube([1, 1.5, 1], center = true);
}

module end_caps() {
    // Left end cap
    color([0.2,0.2,0.2])
    translate([-40, 0, 0])
        cube([2, 14, 35], center = false);

    // Right end cap
    color([0.2,0.2,0.2])
    translate([38, 0, 0])
        cube([2, 14, 35], center = false);
}

module grip_pads() {
    // Rubber grip pattern
    color([0.1,0.1,0.1])
    for (x = [-30:10:30]) {
        translate([x, 7.5, 10])
            cube([8, 1, 15], center = true);
    }
}

// Assembly
union() {
    color([0.7,0.75,0.8]) main_body();
    vial_housing();
    vial_fill();
    color([0.6,0.6,0.6]) measurement_marks();
    end_caps();
    grip_pads();
}

// Square Tool - Engineer Combination Square (realistic style, medium complexity)
// Features ruler blade, anodized head, level vial, knurled grip, scribe pin

$fn = 36;  // Smoother curves for realism

// Overall dimensions
blade_len = 75;
blade_w = 19;
blade_thick = 1.6;
head_size = 30;
head_thick = 20;
grip_d = 12;
grip_h = 18;

module ruler_blade() {
    color("silver")
    difference() {
        // Main blade body
        translate([-blade_len/2, -blade_w/2, -blade_thick/2])
            cube([blade_len, blade_w, blade_thick]);

        // Millimeter scale marks
        for (i = [1:blade_len-1]) {
            h = (i % 10 == 0) ? 3 : 1.5;
            translate([-blade_len/2 + i, -blade_w/2 + 1, -blade_thick/2 - 0.01])
                cube([0.3, h, blade_thick + 0.02]);
        }

        // Number recesses (every 10 mm)
        for (i = [0:10:blade_len-1]) {
            translate([-blade_len/2 + i + 2, -blade_w/2 + 5, -blade_thick/2 - 0.01])
                cube([3, 3, blade_thick + 0.02]);
        }
    }
}

module anodized_head() {
    color([0.1, 0.1, 0.3])  // Dark anodized blue
    difference() {
        // Main head block
        translate([-head_size/2, -head_size/2, -head_thick/2])
            cube([head_size, head_size, head_thick]);

        // Blade slot
        translate([-blade_len/2 - 1, -blade_w/2 - 0.5, -blade_thick/2 - 0.5])
            cube([blade_len + 2, blade_w + 1, blade_thick + 1]);

        // Fillets on edges
        for (x = [-head_size/2 + 2, head_size/2 - 2])
            for (y = [-head_size/2 + 2, head_size/2 - 2])
                translate([x, y, -head_thick/2])
                    cylinder(r=2, h=head_thick);
    }

    // Knurled grip cylinder
    color("silver")
    rotate([90, 0, 0])
        translate([0, 0, head_size/2 + grip_h/2])
        difference() {
            cylinder(d=grip_d, h=grip_h, $fn=60);
            // Knurling grooves
            for (i = [0:30]) {
                rotate([0, 0, i*12])
                    translate([-grip_d/2 - 0.1, -0.5, -0.5])
                        cube([grip_d + 0.2, 1, grip_h + 1]);
            }
        }

    // Level vial
    color([0.8, 0.9, 1, 0.6])  // Transparent blue
    translate([0, 0, head_thick/2 - 4])
        rotate([90, 0, 0])
            cylinder(d=6, h=head_size - 4, $fn=32);

    // Scribe pin hole
    translate([head_size/2 - 4, 0, 0])
        rotate([0, 90, 0])
            cylinder(d=2.5, h=10, $fn=20);
}

// Assembly
ruler_blade();
translate([0, 0, 0])
    anodized_head();

// Realistic 3D Printable Axe - Medium Complexity
// Overall length ~85 mm, suitable for FDM printing

// Color constants
blade_col = "#C0C0C0";
handle_col = "#8B4513";
grip_col = "#2F4F2F";
rivet_col = "#FFD700";

// Main assembly
module axe() {
    union() {
        // Axe head assembly
        translate([0, 0, 5]) axe_head();

        // Handle assembly
        translate([0, 0, -35]) handle();
    }
}

// Axe head with blade and poll
module axe_head() {
    color(blade_col) {
        union() {
            // Main head body
            difference() {
                // Tapered head shape
                hull() {
                    translate([0, 0, 0]) cube([6, 20, 15], center=true);
                    translate([0, 0, 8]) cube([3, 18, 12], center=true);
                }

                // Eye hole for handle
                translate([0, 0, -2])
                    rotate([90, 0, 0])
                        cylinder(h=20, d=8, $fn=32);
            }

            // Cutting edge
            translate([0, 12, 2])
                rotate([0, 10, 0])
                    cube([0.5, 8, 10], center=true);

            // Blade bevel
            difference() {
                translate([0, 12, 2])
                    cube([2, 8, 10], center=true);

                translate([2, 12, 2])
                    rotate([0, -15, 0])
                        cube([4, 9, 11], center=true);
            }

            // Poll (back of axe)
            difference() {
                translate([0, -12, 2])
                    sphere(d=8);

                // Flatten poll slightly
                translate([0, -12, 2])
                    cube([6, 4, 8], center=true);
            }
        }
    }

    // Blade edge highlight
    color("#E8E8E8")
        translate([0, 16.5, 2])
            cube([0.3, 2, 8], center=true);
}

// Handle with grip and wedges
module handle() {
    // Main handle shaft
    color(handle_col) {
        difference() {
            // Tapered handle
            hull() {
                translate([0, 0, 0])
                    cylinder(h=40, d1=8, d2=6, $fn=32);
                translate([0, 0, -30])
                    cylinder(h=30, d1=6, d2=5, $fn=32);
            }

            // Top chamfer
            translate([0, 0, 2])
                cylinder(h=10, d=7, $fn=32);
        }
    }

    // Grip texture
    color(grip_col) {
        for(i = [-25:5:-5]) {
            translate([0, 0, i])
                difference() {
                    cylinder(h=2.5, d=6.2, $fn=32);
                    cylinder(h=3, d=5.8, $fn=32);
                }
        }
    }

    // Wedge at top
    color(handle_col) {
        translate([0, 0, 5])
            rotate([0, 90, 0])
                wedge();
    }

    // Rivet detail
    color(rivet_col) {
        for(i = [-20:10:-10]) {
            translate([2.5, 0, i])
                sphere(d=1.5);
            translate([-2.5, 0, i])
                sphere(d=1.5);
        }
    }
}

// Wedge for securing head
module wedge() {
    difference() {
        cube([3, 8, 4], center=true);
        translate([0, 0, 1])
            cube([2.5, 7, 4], center=true);
    }
}

// Render the complete axe
axe();

