module ruler_blade() {
    color("silver")
    difference() {
        // Main blade body
        translate([-blade_len/2, -blade_w/2, -blade_thick/2])
            cube([blade_len, blade_w, blade_thick]);

        // Millimeter scale marks
        for (i = [1:blade_len-1]) {
            h = (i % 10 == 0) ? 3 : 1.5;
            translate([-blade_len/2 + i, -blade_w/2 + 1, -blade_thick/2 - 0.01])
                cube([0.3, h, blade_thick + 0.02]);
        }

        // Number recesses (every 10 mm)
        for (i = [0:10:blade_len-1]) {
            translate([-blade_len/2 + i + 2, -blade_w/2 + 5, -blade_thick/2 - 0.01])
                cube([3, 3, blade_thick + 0.02]);
        }
    }
}

module anodized_head() {
    color([0.1, 0.1, 0.3])  // Dark anodized blue
    difference() {
        // Main head block
        translate([-head_size/2, -head_size/2, -head_thick/2])
            cube([head_size, head_size, head_thick]);

        // Blade slot
        translate([-blade_len/2 - 1, -blade_w/2 - 0.5, -blade_thick/2 - 0.5])
            cube([blade_len + 2, blade_w + 1, blade_thick + 1]);

        // Fillets on edges
        for (x = [-head_size/2 + 2, head_size/2 - 2])
            for (y = [-head_size/2 + 2, head_size/2 - 2])
                translate([x, y, -head_thick/2])
                    cylinder(r=2, h=head_thick);
    }

    // Knurled grip cylinder
    color("silver")
    rotate([90, 0, 0])
        translate([0, 0, head_size/2 + grip_h/2])
        difference() {
            cylinder(d=grip_d, h=grip_h, $fn=60);
            // Knurling grooves
            for (i = [0:30]) {
                rotate([0, 0, i*12])
                    translate([-grip_d/2 - 0.1, -0.5, -0.5])
                        cube([grip_d + 0.2, 1, grip_h + 1]);
            }
        }

    // Level vial
    color([0.8, 0.9, 1, 0.6])  // Transparent blue
    translate([0, 0, head_thick/2 - 4])
        rotate([90, 0, 0])
            cylinder(d=6, h=head_size - 4, $fn=32);

    // Scribe pin hole
    translate([head_size/2 - 4, 0, 0])
        rotate([0, 90, 0])
            cylinder(d=2.5, h=10, $fn=20);
}

