// OpenSCAD Realistic Phillips #2 Screwdriver Bit
// ~60 mm total length – optimized for FDM 3D printing

// -------------- parameters --------------
bit_d       =  6.3;     // 1/4" hex shank
bit_len     = 19.0;     // depth that goes into driver
tip_len     =  8.0;     // Phillips #2 working tip
tip_dia     =  3.0;     // tip diameter
shank_len   = 30.0;     // visible shank
wall        =  1.2;     // minimum printable wall
$fn         = 36;       // cylinder segmentation

// -------------- colors --------------
color_handle = [0.15,0.15,0.15];   // dark grey steel
color_tip    = [0.75,0.75,0.80];   // bright metal

// -------------- modules --------------

// hexagonal prism generator
module hex(r, h) {
    cylinder(h=h, r=r/cos(30), $fn=6);
}

// knurled grip pattern
module knurl(r, h, rings=8) {
    step = h / rings;
    for (i=[0:rings-1])
        translate([0,0,i*step+step/2])
            rotate([90,0,0])
                difference() {
                    cylinder(h=step*1.1, r=r+0.3, center=true, $fn=6);
                    scale([1,0.4,1]) cylinder(h=step*1.2, r=r+0.4, center=true, $fn=6);
                }
}

// Phillips #2 cross tip
module phillips_tip(l, d) {
    // four flukes 90° apart
    for (a=[0:90:270])
        rotate([0,0,a])
            translate([d/4,0,0])
                rotate([0,90,0])
                    cylinder(h=d*0.55, d=d/3, center=true);
}

// -------------- main model --------------
union() {
    // shank: hex body plus knurl
    color(color_handle)
    union() {
        // main hex shaft
        hex(bit_d/2, bit_len+shank_len);
        // knurled grip zone
        translate([0,0,bit_len])
            knurl(bit_d/2, shank_len*0.7);
    }

    // Phillips #2 tip
    color(color_tip)
    translate([0,0,bit_len+shank_len])
        union() {
            // tapered cone leading to tip
            cylinder(h=tip_len*0.6, d1=bit_d, d2=tip_dia);
            translate([0,0,tip_len*0.6])
                cylinder(h=tip_len*0.4, d=tip_dia);
            // cross recess
            translate([0,0,tip_len*0.6+tip_len*0.4/2])
                phillips_tip(tip_len*0.4, tip_dia);
        }
}

