    color("LightGray") {
        // Shank
        translate([0, 0, -shank_length]) {
            cylinder(h=shank_length, d=shank_diameter, $fn=16);
        }

        // Spiral thread
        for (i = [0:spiral_turns*20]) {
            rotate([0, 0, i*360/(spiral_turns*20)]) {
                translate([shank_diameter/2 + 1, 0, -i*spiral_length/(spiral_turns*20)]) {
                    sphere(d=3, $fn=8);
                }
            }
        }

        // Tapered tip
        translate([0, 0, -shank_length - spiral_length]) {
            cylinder(h=spiral_length, d1=spiral_tip_diameter, d2=shank_diameter, $fn=16);
        }
    }
}

// Handle hole for hanging
module hanging_hole() {
    translate([handle_diameter/2 - 3, 0, handle_length - 10]) {
        rotate([90, 0, 0]) {
            cylinder(h=handle_diameter, d=4, $fn=16);
        }
    }
}

// Assembly
