// Tap Tool - Realistic Style, Medium Complexity
// Overall dimensions: ~90mm length, ~25mm diameter

// Colors
$fn = 50;

module tap_tool() {
    color("silver") {
        union() {
            // Handle section
            translate([0, 0, 75]) 
                rotate([0, 0, 0]) 
                    handle();

            // Shank section
            translate([0, 0, 50]) 
                shank();

            // Threaded section
            translate([0, 0, 0]) 
                    threads();

            // Cutting edges at tip
            translate([0, 0, -5]) 
                cutting_edges();
        }
    }
}

module handle() {
    // Main handle body
    cylinder(h = 25, d1 = 20, d2 = 18);

    // Knurled grip pattern
    difference() {
        cylinder(h = 25, d = 20);
        for (i = [0: 30]) {
            rotate([0, 0, i * 12]) {
                translate([-10.5, -0.5, 0]) 
                    cube([21, 1, 25]);
            }
        }
    }

    // Drive square hole
    color("black") 
        translate([0, 0, 12.5]) 
            cube([6, 6, 12.5], center = true);
}

module shank() {
    // Main shank
    cylinder(h = 25, d = 12);

    // Alignment flats
    difference() {
        cylinder(h = 25, d = 12);
        for (i = [0: 1]) {
            rotate([0, 0, i * 90]) {
                translate([5, -1, 0]) 
                    cube([10, 2, 25]);
            }
        }
    }
}

module threads() {
    // Thread core
    cylinder(h = 50, d = 8);

    // Thread profile
    for (i = [0: 20]) {
        translate([0, 0, i * 2.5]) {
            rotate([0, 0, i * 30]) {
                // Thread ridge
                difference() {
                    cylinder(h = 2.5, d1 = 10, d2 = 9.5);
                    cylinder(h = 2.5, d = 8);
                }
            }
        }
    }

    // Flutes for chip clearance
    for (i = [0: 3]) {
        rotate([0, 0, i * 90]) {
            difference() {
                translate([0, 0, 25]) 
                    cylinder(h = 50, d = 10);
                translate([-5, -5, 0]) 
                    cube([10, 10, 50]);
            }
        }
    }
}

module cutting_edges() {
    // Tapered cutting end
    cylinder(h = 5, d1 = 8, d2 = 6);

    // Cutting faces
    for (i = [0: 3]) {
        rotate([0, 0, i * 90 + 45]) {
            translate([0, 0, 2.5]) {
                rotate([90, 0, 0]) {
                    // Relief angle
                    translate([-2, -2.5, -2]) 
                        cube([4, 5, 4]);
                }
            }
        }
    }
}

