// Die Tool - Realistic Style, Medium Complexity
// A 6-sided die with chamfered edges and inset pips
// Size: 60mm cube with 3mm wall thickness
// Pip diameter: 8mm, depth: 3mm

// Main color definitions
die_color = [0.9, 0.9, 0.9];  // Ivory white
pip_color = [0.1, 0.1, 0.1];  // Deep black

// Overall dimensions
die_size = 60;
edge_chamfer = 3;
wall_thickness = 3;

// Pip dimensions
pip_diameter = 8;
pip_depth = 3;

// Create the main die body
module die_body() {
    difference() {
        // Main cube with chamfered edges
        difference() {
            cube([die_size, die_size, die_size], center = true);

            // Chamfer all 12 edges
            // Top edges
            translate([0, 0, die_size/2 - edge_chamfer]) rotate([0, 0, 45]) 
                cube([die_size*sqrt(2), 2*edge_chamfer, 2*edge_chamfer], center = true);
            translate([0, 0, die_size/2 - edge_chamfer]) rotate([0, 0, -45]) 
                cube([die_size*sqrt(2), 2*edge_chamfer, 2*edge_chamfer], center = true);

            // Bottom edges
            translate([0, 0, -die_size/2 + edge_chamfer]) rotate([0, 0, 45]) 
                cube([die_size*sqrt(2), 2*edge_chamfer, 2*edge_chamfer], center = true);
            translate([0, 0, -die_size/2 + edge_chamfer]) rotate([0, 0, -45]) 
                cube([die_size*sqrt(2), 2*edge_chamfer, 2*edge_chamfer], center = true);

            // Side edges
            translate([0, die_size/2 - edge_chamfer, 0]) rotate([0, 0, 45]) rotate([90, 0, 0])
                cube([die_size*sqrt(2), 2*edge_chamfer, 2*edge_chamfer], center = true);
            translate([0, -die_size/2 + edge_chamfer, 0]) rotate([0, 0, 45]) rotate([90, 0, 0])
                cube([die_size*sqrt(2), 2*edge_chamfer, 2*edge_chamfer], center = true);

            // Front/back edges
            translate([die_size/2 - edge_chamfer, 0, 0]) rotate([0, 0, 45]) rotate([0, 90, 0])
                cube([die_size*sqrt(2), 2*edge_chamfer, 2*edge_chamfer], center = true);
            translate([-die_size/2 + edge_chamfer, 0, 0]) rotate([0, 0, 45]) rotate([0, 90, 0])
                cube([die_size*sqrt(2), 2*edge_chamfer, 2*edge_chamfer], center = true);
        }

        // Hollow out the center for 3D printing
        cube([die_size - 2*wall_thickness, die_size - 2*wall_thickness, die_size - 2*wall_thickness], center = true);
    }
}

// Create a single pip (dot)
module pip() {
    difference() {
        cylinder(h = pip_depth + 0.1, d = pip_diameter, $fn = 32);
        translate([0, 0, pip_depth]) 
            cylinder(h = 0.1, d = pip_diameter + 0.2, $fn = 32);
    }
}

// Create pips for face 1 (1 dot)
module face_1() {
    color(pip_color) {
        translate([0, 0, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
    }
}

// Create pips for face 2 (2 dots)
module face_2() {
    color(pip_color) {
        translate([-die_size/4, -die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
        translate([die_size/4, die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
    }
}

// Create pips for face 3 (3 dots)
module face_3() {
    color(pip_color) {
        translate([-die_size/4, -die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
        translate([0, 0, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
        translate([die_size/4, die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
    }
}

// Create pips for face 4 (4 dots)
module face_4() {
    color(pip_color) {
        translate([-die_size/4, -die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
        translate([die_size/4, -die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
        translate([-die_size/4, die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
        translate([die_size/4, die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
    }
}

// Create pips for face 5 (5 dots)
module face_5() {
    color(pip_color) {
        translate([-die_size/4, -die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
        translate([die_size/4, -die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
        translate([0, 0, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
        translate([-die_size/4, die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
        translate([die_size/4, die_size/4, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
    }
}

// Create pips for face 6 (6 dots)
module face_6() {
    color(pip_color) {
        for (x = [-die_size/4, die_size/4]) {
            for (y = [-die_size/3, 0, die_size/3]) {
                translate([x, y, die_size/2 - pip_depth/2]) rotate([0, 180, 0]) pip();
            }
        }
    }
}

