color_handle = [0.15,0.15,0.15];  // rubberised grip
color_jaw    = [0.9,0.9,0.9];     // aluminium jaw face

// ---------- helper: rounded cube ----------
module rounded_cube(size, r, center=false) {
    x = size.x; y = size.y; z = size.z;
    translate(center?[0,0,0]:[x/2,y/2,z/2])
    hull() {
        for(dx=[-1,1], dy=[-1,1], dz=[-1,1])
            translate([dx*(x/2-r),dy*(y/2-r),dz*(z/2-r)])
            sphere(r=r);
    }
}

// ---------- base ----------
module vise_base() {
    color(color_cast)
    difference() {
        rounded_cube([base_length,base_width,base_height], 3, true);
        // mounting slots
        for(x=[-base_length/3, base_length/3])
            translate([x,0,-base_height/2+1])
            cube([5, base_width-4, 2], center=true);
    }
}

// ---------- sliding jaw body ----------
module sliding_jaw() {
    color(color_cast)
    difference() {
        union() {
            // main block
            translate([0,0,base_height/2+slide_height/2])
                rounded_cube([slide_length,slide_width,slide_height], 2, true);
            // vertical jaw
            translate([slide_length/2-2,0,base_height+slide_height/2+jaw_height/2])
                rounded_cube([slide_length/2, jaw_width, jaw_height], 2, true);
        }
        // leadscrew clearance hole
        translate([-slide_length/2-1,0,base_height/2+slide_height/2])
            rotate([0,90,0])
            cylinder(h=slide_length+2, d=leadscrew_d+1, center=true);
    }
}

// ---------- fixed jaw ----------
module fixed_jaw() {
    color(color_cast)
    translate([-base_length/2,0,base_height+jaw_height/2])
        rounded_cube([base_length/2, jaw_width, jaw_height], 2, true);
}

// ---------- replaceable jaw plates ----------
module jaw_plate(dx) {
    color(color_jaw)
    translate([dx,0,base_height+jaw_height/2-0.5])
        cube([2, jaw_width-4, jaw_height-2], center=true);
}

// ---------- knurled handle ----------
module handle() {
    color(color_handle)
    union() {
        // shaft
        rotate([0,90,0])
            cylinder(h=handle_len, d=handle_d, center=true);
        // grips
        for(i=[-handle_len/2+4 : 8 : handle_len/2-4])
            translate([i,0,0])
                rotate([0,90,0])
                    cylinder(h=handle_grip_d, d=handle_d+4, $fn=6);
    }
}

// ---------- leadscrew assembly ----------
module leadscrew() {
    color(color_steel)
    union() {
        // threaded rod
        rotate([0,90,0])
            cylinder(h=base_length+10, d=leadscrew_d, center=true);
        // crank end
        translate([base_length/2+handle_len/2+5,0,0])
            handle();
    }
}

// ---------- guiding rails underneath ----------
module rails() {
    color(color_steel)
    for(y=[-slide_width/4, slide_width/4])
        translate([0,y,base_height/2-1])
            cube([slide_length, 4, 2], center=true);
}

