                cube([jaw_width * 0.9, jaw_thickness + 1, jaw_length * 1.5], center = true);
        }

        // Serrations
        for (i = [0:2:jaw_length - 10]) {
            translate([0, 0, -jaw_length / 2 + i + 5])
                cube([jaw_width * 0.95, jaw_thickness + 0.1, 0.5], center = true);
        }
    }
}

module grip_pad() {
    color(color_grip) {
        difference() {
            // Main pad
            translate([0, 0, 0])
                cube([grip_pad_width, grip_pad_thickness, grip_pad_length], center = true);

            // Grooves for grip
            for (i = [-grip_pad_length / 2 + 3:3:grip_pad_length / 2 - 3]) {
                translate([0, 0, i])
                    cube([grip_pad_width, grip_pad_thickness + 0.1, 0.8], center = true);
            }
        }
    }
}

module screw() {
    color(color_metal) {
        // Screw head
        cylinder(h = screw_height, r = screw_radius, $fn = 6);

        // Threaded shaft
        translate([0, 0, -5])
            cylinder(h = 10, r = screw_radius * 0.7, $fn = 16);
    }
}

module tong_half() {
    union() {
        // Handle assembly
        translate([0, 0, 0])
            rotate([90, 0, 0])
                handle();

        // Transition piece
        translate([0, handle_diameter / 2, handle_length / 2])
            rotate([0, 90, 0])
                cylinder(h = handle_diameter, r = handle_diameter / 2, $fn = 16);

        // Jaw
        translate([0, handle_diameter, handle_length])
            rotate([0, 0, 0])
                jaw();

        // Grip pad on jaw
        translate([0, handle_diameter + jaw_thickness / 2 + grip_pad_thickness / 2 + 1, handle_length + 5])
            rotate([90, 0, 0])
                grip_pad();

        // Hinge hole (will be aligned when mirrored)
        translate([0, handle_diameter / 2, handle_length - 5])
            rotate([0, 90, 0])
                cylinder(h = handle_diameter + 2, r = screw_radius * 0.8, $fn = 16);
    }
}

// Assembly
