    union() {
        // First half
        tong_half();

        // Mirrored second half
        mirror([0, 1, 0]) {
            tong_half();
        }
    }

    // Remove hinge material for screw
    translate([0, 0, handle_length - 5])
        rotate([0, 90, 0])
            cylinder(h = handle_diameter + 2, r = screw_radius * 0.8, $fn = 16);
}

