// Vernier calipers module
module calipers() {
    // Main beam/ruler
    color(color_body) {
        translate([-40, 0, 0]) {
            difference() {
                // Main beam body
                cube([80, 15, 6], center=false);

                // Center slot for slider
                translate([5, wall, -1]) cube([70, 15 - 2*wall, 8]);

                // Measurement scale markings
                for(i = [0:79]) {
                    translate([-39.5 + i, 0.5, 3.1]) cube([0.3, 1, 0.2]);
                }
            }
        }
    }

    // Fixed jaw
    color(color_jaw) {
        translate([-40, 15, 0]) {
            union() {
                // Jaw base
                cube([8, 10, 6]);

                // Measuring face
                translate([7.9, 0, 0]) cube([0.1, 10, 6]);
            }
        }
    }

    // Sliding jaw assembly
    color(color_body) {
        translate([-5, 0, 0]) {
            difference() {
                // Slider body
                cube([20, 15, 15]);

                // Grip cutouts
                for(i = [0:3]) {
                    translate([2 + i*4, 1, 2]) cube([2, 13, 2]);
                }

                // Thumb wheel hole
                translate([10, -2, 7.5]) rotate([0, 90, 0]) 
                    cylinder(h=4, r=3, center=true);
            }

            // Lower jaw
            translate([0, 15, 0]) {
                union() {
                    cube([8, 10, 6]);
                    translate([7.9, 0, 0]) cube([0.1, 10, 6]);
                }
            }

            // Thumb wheel
            color(color_highlight) {
                translate([10, -2, 7.5]) rotate([0, 90, 0]) {
                    cylinder(h=3, r=3.5);
                    for(i = [0:7]) {
                        rotate([0, 0, i*45]) 
                            translate([3.5, 0, 0]) cube([0.5, 0.5, 3]);
                    }
                }
            }

            // Locking screw
            color(color_highlight) {
                translate([15, 7.5, 15]) rotate([90, 0, 0]) {
                    cylinder(h=5, r=2);
                    translate([0, 0, 5]) cylinder(h=2, r=4, $fn=6);
                }
            }
        }
    }

    // Vernier scale
    color(color_scale) {
        translate([-5, -1, 3.1]) {
            for(i = [0:9]) {
                translate([i*2, 0, 0]) cube([0.2, 0.8, 0.2]);
            }
        }
    }

    // Depth rod
    color(color_jaw) {
        translate([-38, 7.5, -20]) {
            cube([2, 2, 20]);
            translate([0, 0, -1]) cylinder(h=2, r=1);
