color_pivot = [0.8, 0.8, 0.8]; // Silver metal
color_tip = [0.9, 0.9, 0.9]; // Polished steel

// Main assembly
module dividers() {
    union() {
        // First leg
        translate([0, 0, 0]) {
            color(color_leg) leg();
        }

        // Second leg (mirrored)
        rotate([0, 0, 180]) {
            color(color_leg) leg();
        }

        // Pivot mechanism
        translate([0, 0, 0]) {
            color(color_pivot) pivot();
        }

        // Adjustment wheel
        translate([0, 0, leg_thickness]) {
            color(color_pivot) adjustment_wheel();
        }
    }
}

// Single leg module
module leg() {
    union() {
        // Handle grip
        translate([-leg_length/2 + 5, 0, -handle_length/2]) {
            rotate([90, 0, 0]) {
                color(color_handle) cylinder(h=handle_length, d=handle_diameter, center=true);
            }
        }

        // Handle end cap
        translate([-leg_length/2, 0, 0]) {
            sphere(d=handle_diameter);
        }

        // Main leg bar
        translate([0, 0, -leg_thickness/2]) {
            cube([leg_length, leg_width, leg_thickness], center=true);
        }

        // Gripping ridges on handle
        for (i = [-5:2:5]) {
            translate([-leg_length/2 + 12, 0, i]) {
                rotate([90, 0, 0]) {
                    difference() {
                        cylinder(h=1, d=handle_diameter + 1, center=true);
                        cylinder(h=2, d=handle_diameter - 1, center=true);
                    }
                }
            }
        }

        // Pointed tip
        translate([leg_length/2 - tip_length/2, 0, 0]) {
            rotate([0, 90, 0]) {
                cylinder(h=tip_length, r1=leg_width/2, r2=0.5, center=true);
            }
        }
    }
}

// Pivot mechanism
module pivot() {
    difference() {
        // Main pivot body
        cylinder(h=leg_thickness * 2 + 2, r=pivot_radius, center=true);

        // Central hole
        cylinder(h=leg_thickness * 2 + 4, r=2, center=true);
    }

    // Threaded part detail
    translate([0, 0, leg_thickness + 1]) {
        cylinder(h=3, r1=pivot_radius - 1, r2=pivot_radius - 1.5);
    }
}

// Adjustment wheel
module adjustment_wheel() {
    difference() {
        // Wheel body
        cylinder(h=4, r=8, center=true);

        // Knurling pattern
        for (i = [0:15:360]) {
            rotate([0, 0, i]) {
                translate([6, 0, -2]) {
                    cube([4, 1, 5], center=true);
                }
            }
        }

        // Central hole
        cylinder(h=5, r=2.5, center=true);
    }
}

