// Tang connecting handle to blade
module tang() {
    translate([0, 0, -tang_length/2 + 5]) {
        // Main tang
        cube([8, blade_thickness + 2, tang_length], center=true);

        // Rivet holes
        for(i = [1:3]) {
            translate([0, 0, -tang_length/2 + i * 12])
                rotate([90, 0, 0])
                    cylinder(h=10, d=3, center=true);
